import React from 'react';
import styled from 'styled-components';

const Modal = ({ isOpen, onClose, children, modalType }) => {
    if (!isOpen) return null; // 모달이 열려 있지 않으면 아무것도 렌더링하지 않음

    return (
        <ModalOverlay modalType={modalType} onClick={onClose}>
            <ModalContent modalType={modalType} onClick={(e) => e.stopPropagation()}> {/* 클릭 이벤트 전파 방지 */}
                <CloseButton onClick={onClose}>X</CloseButton>
                {children}
            </ModalContent>
        </ModalOverlay>
    );
};

// 스타일 컴포넌트 정의
const ModalOverlay = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.2);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 1000; // 모달을 최상위로 표시
    border: 2px solid #ddd;
    border-radius: 30px 30px 1px 30px; //반시계 ㅔ방향


    ${({ modalType }) => modalType === 'nav' && `
    background: rgba(0, 0, 0, 0.2);
    top: 20%;
    background: none;




`}
`;

const ModalContent = styled.div`
    background: white;
    padding: 50px;
    border-radius: 30px 30px 5px 30px;
    width: 300px;
    border: 2px solid #62B9EC;

    /* 조건부 스타일링 */
    ${({ modalType }) => modalType === 'nav' && `
        background: #f3f3f3; /* 다른 배경색 */
        border: 2px solid #0080ff; /* 다른 테두리 */
    `}
`;

const CloseButton = styled.button`
    background: transparent;
    border: none;
    cursor: pointer;
    font-size: 12px;
    float: right;
`;

export default Modal;
