import React, { useState } from 'react';
import styled from 'styled-components';
import Head from "../../components/Head";
import { useNavigate } from 'react-router-dom';

const WritePage = () => {
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [participants, setParticipants] = useState(0);
  const [deadline, setDeadline] = useState('');
  const [progress, setProgress] = useState('');
  const [projectRole, setProjectRole] = useState('');
  const [description, setDescription] = useState('');

  const handleSave = () => {
    console.log({
      title,
      participants,
      deadline,
      progress,
      projectRole,
      description,
    });
    navigate('/'); 
  };

  return (
    <>
      <Head />
      <WriteWrapper>
        <BackButton onClick={() => navigate(-1)}>◀ 돌아가기</BackButton>

        <Form>
          <TitleInput>
            <InputField
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="제목을 적어주세요"
            />
          </TitleInput>
        </Form>

        <Container>
          <Form> 
            <Title>
              <Label>모집구분 | 프로젝트</Label>
            </Title> 

            <InputBox>
              {[
                { label: '모집인원', type: 'number', value: participants, setter: setParticipants },
                { label: '마감일', type: 'date', value: deadline, setter: setDeadline },
                { label: '진행장소', type: 'text', value: progress, setter: setProgress },
                { label: '모집 역할', type: 'text', value: projectRole, setter: setProjectRole },
                { label: '진행기간', type: 'text', value: projectRole, setter: setProjectRole },
              ].map(({ label, type, value, setter }) => (
                <InputWrapper key={label}>
                  <Label>{label}</Label>
                  <Input
                    type={type}
                    value={value}
                    onChange={(e) => setter(e.target.value)}
                    placeholder={label}
                  />
                </InputWrapper>
              ))}
            </InputBox>
          </Form>
        </Container>

        <Body>
          <Form>
            <TextArea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="본문을 작성해주세요"
            />
          </Form>
        </Body>

        <Submit>
          <SaveButton onClick={handleSave}>임시저장</SaveButton>
          <SaveButton onClick={handleSave}>저장하기</SaveButton>
        </Submit>
      </WriteWrapper>
    </>
  );
};

export default WritePage;

const WriteWrapper = styled.div`
  padding: 20px;
  margin-top: calc(100vh - 35%);
  min-height: calc(100vh - 250px);
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BackButton = styled.button`
  position: absolute;
  left: 10%;
  margin-bottom: 20px;
`;

const Form = styled.div`
  gap: 10px;
`;

const Label = styled.label`
  font-weight: bold;
`;

const TitleInput = styled.div`
  position: absolute;
  left: 20%;
  top: 30%;
  align-items: center;
  border-bottom: 3px solid #ccc;
  cursor: text;
  width: 60%;

  &:focus-within {
    border-bottom: 3px solid #62b9ec;
  }
`;

const InputField = styled.input`
  border: none;
  outline: none;
  padding: 5px 0;
  font-size: 25px;
  width: 100%;

  &::placeholder {
    color: #aaa;
  }
`;

const Container = styled.div`
  justify-content: space-between;
  width: 90%; 
  margin-top: 100px;
  padding: 20px;
  border: 3px solid #ddd;
  border-radius: 15px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: white;

  @media (max-width: 768px) {
    width: 100%;
  }
`;

const Title = styled.label`
  font-weight: bold;
  margin-bottom: 10px;
`;

const InputBox = styled.div`
  display: flex;
  flex-wrap: wrap; 
  justify-content: space-between; 
  width: 100%; 
