import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import {useLocation, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faSlidersH } from '@fortawesome/free-solid-svg-icons';



const Nav = () => {

  // const navigate = useNavigate();

  //   const handleAddButtonClick = () => {
  //       navigate('/writePage'); // writePageÎ°ú Ïù¥Îèô
  //   };

  const [visible, setVisible] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);

  // const [toggleActive, setToggleActive] = useState(false); // ÌÜ†Í∏Ä ÏÉÅÌÉú Ï∂îÍ∞Ä
  //Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú Ï¥àÍ∏∞Í∞í Í∞ÄÏ†∏Ïò§Í∏∞
  const [toggleActive, setToggleActive] = useState(() => {
    // Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú Ï¥àÍ∏∞Í∞í Í∞ÄÏ†∏Ïò§Í∏∞
    const storedToggleState = localStorage.getItem('toggleActive');
    return storedToggleState !== null ? JSON.parse(storedToggleState) : false;
  });


  const [filterVisible, setFilterVisible] = useState(false); // ÌïÑÌÑ∞ Í∞ÄÏãúÏÑ± ÏÉÅÌÉú Ï∂îÍ∞Ä

  const handleScroll = () => {
    if (typeof window !== 'undefined') {
      const currentScrollY = window.scrollY;
      setVisible(lastScrollY > currentScrollY || currentScrollY < 10);
      setLastScrollY(currentScrollY);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [lastScrollY]);


  useEffect(() => {
    // Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú ÌÜ†Í∏Ä ÏÉÅÌÉú Í∞ÄÏ†∏Ïò§Í∏∞
    const storedToggleState = localStorage.getItem('toggleActive');
    if (storedToggleState !== null) {
      setToggleActive(JSON.parse(storedToggleState));
    }
  }, []);

  useEffect(() => {
    // ÌÜ†Í∏Ä ÏÉÅÌÉúÍ∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóê Ï†ÄÏû•
    localStorage.setItem('toggleActive', JSON.stringify(toggleActive));
  }, [toggleActive]);



  const handleToggleChange = () => {
    setToggleActive(!toggleActive); // ÌÜ†Í∏Ä ÏÉÅÌÉú Î≥ÄÍ≤Ω
  };

  // const handleToggleChange = () => {
  //   setToggleActive((prev) => !prev);
  // };   // Ïù¥Ï†Ñ ÏÉÅÌÉú Í∏∞Î∞ò


  const toggleFilterVisibility = () => {
    setFilterVisible(!filterVisible); // ÌïÑÌÑ∞ Í∞ÄÏãúÏÑ± ÌÜ†Í∏Ä
  };



  return (
    <NavWrapper visible={visible}>
      <Logo onClick={() => (window.location.href = "/")}>
        <span>iTeaMoa</span>
      </Logo>
      <ToggleContainer>
        <ToggleLabel active={toggleActive}>STUDY</ToggleLabel>
        <ToggleSwitch type="checkbox" checked={toggleActive} onChange={handleToggleChange} />
        <ToggleLabel active={!toggleActive}>PROJECT</ToggleLabel>
      </ToggleContainer>
      <SearchContainer>
        <SearchInput placeholder="Search projects" />
        <SearchIcon><FontAwesomeIcon icon={faSearch} /></SearchIcon>
      </SearchContainer>

      <FilterIcon onClick={toggleFilterVisibility}>
        <FontAwesomeIcon icon={faSlidersH} />
      </FilterIcon>


    {filterVisible && (
      <TagContainer>
        <Tag>AWS</Tag>
        <Tag>Blockchain</Tag>
        <Tag>NodeJS</Tag>
        <Tag>React</Tag>
        <Tag>Java</Tag>
      </TagContainer>
       )}


      <UserActions>
        <SignUp>SignUp</SignUp>
        <Login>Login</Login>
      </UserActions>
    </NavWrapper>
  );
}

export default Nav;

