import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import {useLocation, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faSlidersH } from '@fortawesome/free-solid-svg-icons';




const Nav = () => {

  const [visible, setVisible] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);
  const [searchValue, setSearchValue] = useState("");
  const [modalVisible, setModalVisible] = useState(false);
  const navigate = useNavigate();

  // const [toggleActive, setToggleActive] = useState(false); // 토글 상태 추가
  //로컬 스토리지에서 초기값 가져오기
  const [toggleActive, setToggleActive] = useState(() => {
    // 로컬 스토리지에서 초기값 가져오기
    const storedToggleState = localStorage.getItem('toggleActive');
    return storedToggleState !== null ? JSON.parse(storedToggleState) : false;
  });


  const [filterVisible, setFilterVisible] = useState(false); // 필터 가시성 상태 추가
  const [selectedTags, setSelectedTags] = useState([]); // 선택된 태그 상태 추가

  const auth = getAuth();



  const handleScroll = () => {
    if (typeof window !== 'undefined') {
      const currentScrollY = window.scrollY;
      setVisible(lastScrollY > currentScrollY || currentScrollY < 10);
      setLastScrollY(currentScrollY);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [lastScrollY]);


  useEffect(() => {
    // 로컬 스토리지에서 토글 상태 가져오기
    const storedToggleState = localStorage.getItem('toggleActive');
    if (storedToggleState !== null) {
      setToggleActive(JSON.parse(storedToggleState));
    }
  }, []);

  // useEffect(() => {
  //   // 토글 상태가 변경될 때마다 로컬 스토리지에 저장
  //   localStorage.setItem('toggleActive', JSON.stringify(toggleActive));
  // }, [toggleActive]);

  const handleToggleChange = () => {
    setToggleActive(!toggleActive); // 토글 상태 변경
  };

  // const handleToggleChange = () => {
  //   setToggleActive((prev) => !prev);
  // };   // 이전 상태 기반


  const toggleFilterVisibility = () => {
    setFilterVisible(!filterVisible); // 필터 가시성 토글
  };



  const handleChange = (e) => {
    setSearchValue(e.target.value);
    // navigate(`/search?q=${e.target.value}`);
    console.log('e.target.value', e.target.value)

  }


  const handleAddButtonClick = () => {
    navigate('/SearchPage'); // SearchPage로 이동
  };



  const logAddButtonClick = () => {
    navigate('/LoginPage'); // SearchPage로 이동
  };



  const signAddButtonClick = () => {
    navigate('/SignupPage'); // SearchPage로 이동
  };



  const handleTagClick = (tag) => {
    setSelectedTags((prev) =>
      prev.includes(tag) ? prev.filter((t) => t !== tag) : [...prev, tag]
    );
  };

  // const toggleModalVisibility = () => {
  //   setModalVisible(!modalVisible);
  // };


  const toggleModalVisibility = () => {
    setFilterVisible(!filterVisible); // 필터 모달 가시성 토글
  };



  return (
    <NavWrapper visible={visible}>
      <Logo>
      <img
          alt="Disney Plus Logo"
          src="/images/logo1.png"
        onClick={() => (window.location.href = "/")}/>
      </Logo>

      <ToggleContainer>
        <ToggleLabel active={toggleActive}>STUDY</ToggleLabel>
        <ToggleSwitch type="checkbox" checked={toggleActive} onChange={handleToggleChange} />
        <ToggleLabel active={!toggleActive}>PROJECT</ToggleLabel>
      </ToggleContainer>
      <SearchContainer>
        <SearchInput value={searchValue}
        onChange={handleChange} placeholder="Search projects" />
        <SearchIcon onClick={handleAddButtonClick}><FontAwesomeIcon icon={faSearch} /></SearchIcon>

        <FilterIcon onClick={toggleFilterVisibility}>
        <FontAwesomeIcon icon={faSlidersH} />
      </FilterIcon>

      </SearchContainer>

{/* 
    {filterVisible && (
      <ModalOverlay onClick={toggleModalVisibility}>
       <ModalContent onClick={(e) => e.stopPropagation()}> 
      <TagContainer>
        {['AWS', 'Blockchain', 'NodeJS', 'React', 'Java'].map((tag) => (
            <Tag
              key={tag}
              onClick={() => handleTagClick(tag)}
              selected={selectedTags.includes(tag)} // 선택 상태에 따라 스타일 변경
            >
              {tag}
            </Tag>
          ))}
      </TagContainer>
      </ModalContent> 
        </ModalOverlay>
       )} */}


{filterVisible && (
      <TagContainer>
         {['AWS', 'Blockchain', 'NodeJS', 'React', 'Java'].map((tag) => (
            <Tag
              key={tag}
              onClick={() => handleTagClick(tag)}
              selected={selectedTags.includes(tag)} // 선택 상태에 따라 스타일 변경
            >
              {tag}
            </Tag>
          ))}
      </TagContainer>
       )}

      <UserActions>
        <SignUp onClick={signAddButtonClick}>SignUp</SignUp>
        <Login onClick={logAddButtonClick} >Login</Login>
      </UserActions>
    </NavWrapper>
  );
}

export default Nav;

// Styled Components
const NavWrapper = styled.nav`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 35%;
  background-color: #ffffff;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 36px;
  // box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  z-index: 3;
  transition: transform 0.5s ease;
  transform: translateY(${props => (props.visible ? '0' : '-100%')});
`;



const Logo = styled.div`
  position: absolute;
  top:5%;

  img {
    width: 90%; /* 부모 요소의 너비에 맞게 조정 */
    max-width: 300px; /* 최대 너비 설정 */
    height: auto; /* 비율 유지 */
    cursor: pointer;
  }
`;



const ToggleContainer = styled.div`
  display: flex;
  align-items: center;
  position: absolute;
  top: 35%;
  cursor: pointer;
  font-size: 24px;
`;

const ToggleLabel = styled.label`
  margin: 0 10px;
  font-size: 16px;
  font-weight: bold;
  color: ${props => (props.active ? '#aaa' : '#0080ff')}; // 활성화 여부에 따라 색상 변경
`;

const ToggleSwitch = styled.input`
  appearance: none;
  width: 50px;
  height: 30px;
  background: #ccc; 
  border-radius: 30px; 
  position: relative;
  cursor: pointer;
  outline: none;

  &:checked {
    background: #62B9EC; 
  }

  &:checked::before {
    transform: translateX(24px); 
  }

  &::before {
    content: '';
    position: absolute;
    width: 26px; 
    height: 26px; 
    top: 7%;
    border-radius: 50%; 
    background: white; 
    transition: transform 0.2s; 
  }
`;

const SearchContainer = styled.div`
  position: absolute;
  display: flex;
  align-items: center;
  margin-right: 20px; /* 간격 조정 */
  margin-top: 100px;
  width: 400px; /* 검색창의 전체 너비를 설정 */
`;

const SearchInput = styled.input`
  width: 100%; /* 너비를 100%로 설정하여 길이 늘리기 */
  padding: 15px 40px;
  border: 2px solid #0080ff;
  border-radius: 15px;
  outline: none;
  font-size: 16px;
  background-color: #F3F0F0;

  &::placeholder {
    color: #aaa;
  }
`;

const SearchIcon = styled.span`
  position: absolute;
  right: 20%;
  top: 50%;
  transform: translate(250%, -50%);
  font-size: 20px;
