import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser as regularUser } from '@fortawesome/free-regular-svg-icons'; 
import LikeButton from '../../components/LikeButton';

//searchpage에서 itemslist 가져오기
const SearchFeed = ({ itemList, setSearchResults }) => {
  const navigate = useNavigate();

  const handleProjectClick = (project) => {
    navigate(`/ApplyPage/${project.pk}`);
  };



  const handleLikeClick = (index, newLiked, newLikesCount) => {
    setSearchResults((prevProjects) => {
      const newProjects = [...prevProjects];
      const project = newProjects[index];
      project.liked = newLiked;
      project.likesCount = newLikesCount;
      return newProjects;
    });
  };


  return (
    <SectionWrapper>
      <ProjectList>
        {itemList.length === 1 ? ( // 검색 결과가 하나일 때
          <CenteredProjectCard>
            <ProjectCard>
              <ProjectOwner>
                <FontAwesomeIcon icon={regularUser} style={{ fontSize: '20px', lineHeight: '1.2', marginRight: '6px' }} />
                {itemList[0].creatorID}
              </ProjectOwner>

            <LikeButtonWrapper>
            <LikeButton 
                initialLiked={itemList[0].liked} // itemList[0]의 liked 속성 사용
                initialLikesCount={itemList[0].likesCount} // itemList[0]의 likesCount 사용
                onLikeChange={(newLiked, newLikesCount) => handleLikeClick(0, newLiked, newLikesCount)} // index 0 사용
                apiEndpoint="/main/like" // MainPage API 엔드포인트
                pk={itemList[0].pk} 
                sk={itemList[0].sk}
              />
            </LikeButtonWrapper>

              <ProjectTitle>{itemList[0].title}</ProjectTitle>
              <Content>{itemList[0].content}</Content>
              <ProjectInfo>
                <Tags>
                  {itemList[0].tags.map((tag, tagIndex) => (
                    <Tag key={tagIndex}>{tag}</Tag>
                  ))}
                </Tags>
                <Details>
                  모집인원 | {itemList[0].recruitmentNum}명
                </Details>
                <Details>
                  마감일 | {new Date(itemList[0].deadLine).toLocaleDateString()}
                </Details>
              </ProjectInfo>
              <ApplyButton onClick={() => handleProjectClick(itemList[0])}>신청하기</ApplyButton>
            </ProjectCard>
          </CenteredProjectCard>
        ) : (
          itemList.map((project, index) => ( // 검색 결과가 여러 개일 때
            <ProjectCard key={index}>
              <ProjectOwner>
                <FontAwesomeIcon icon={regularUser} style={{ fontSize: '20px', lineHeight: '1.2', marginRight: '6px' }} />
                {project.creatorID}
              </ProjectOwner>
              <LikeButtonWrapper>
            <LikeButton 
                initialLiked={itemList.liked} // itemList[0]의 liked 속성 사용
                initialLikesCount={itemList.likesCount} // itemList[0]의 likesCount 사용
                onLikeChange={(newLiked, newLikesCount) => handleLikeClick(index, newLiked, newLikesCount)} // index 0 사용
                apiEndpoint="/main/like" // MainPage API 엔드포인트
                pk={itemList.pk} 
                sk={itemList.sk}
              />
            </LikeButtonWrapper>
              <ProjectTitle>{project.title}</ProjectTitle>
              <Content>{project.content}</Content>
              <ProjectInfo>
                <Tags>
                  {project.tags.map((tag, tagIndex) => (
                    <Tag key={tagIndex}>{tag}</Tag>
                  ))}
                </Tags>
                <Details>
                  모집인원 | {project.recruitmentNum}명
                </Details>
                <Details>
                  마감일 | {new Date(project.deadLine).toLocaleDateString()}
                </Details>
              </ProjectInfo>
              <ApplyButton onClick={() => handleProjectClick(project)}>신청하기</ApplyButton>
            </ProjectCard>
          ))
        )}
      </ProjectList>
    </SectionWrapper>
  );
};

const SectionWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  margin-top: 80px;
  margin-bottom: 40px;
`;

// const SectionTitle = styled.h2`
//   font-size: 24px;
//   margin-top: 20px;
//   margin-bottom: 20px;
// `;

const ProjectList = styled.div`
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  justify-content: center;
  width: 50%;
  max-height: 150vh;
  max-width: 1200px;

  @media (max-width: 1200px) {
    width: 80%;
  }

  @media (max-width: 768px) {
    width: 90%;
  }

  @media (max-width: 480px) {
    width: 100%;
  }
`;

const CenteredProjectCard = styled.div`
  display: flex;
  justify-content: center; 
  width: 100%;
`;

const ProjectCard = styled.div`
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  border: 2px solid #A0DAFB;
  border-radius: 30px 30px 1px 30px;
  padding: 15px;
  margin-top: 10px;
  margin-bottom: 10px;
  margin-right: 30px;
  width: calc(100%/2 - 50px);
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: white;
  min-height: 200px;
  max-height: 800px;



  @media (max-width: 1200px) {

    max-height: 500px;
  }

  @media (max-width: 768px) {

    margin: 15px 5px;
    max-height: 400px; 
  }

  @media (max-width: 480px) {

    margin: 10px 0; 
    max-height: 300px; 
  }


  &:hover {
    background-color: #A0DAFB;
  }

  @media (max-width: 1200px) {
    width: calc(33.33% - 20px); /* 3열로 조정 */
  }

  @media (max-width: 768px) {
    width: calc(50% - 20px); /* 2열로 조정 */
  }

  @media (max-width: 480px) {
    width: 100%; /* 1열로 조정 */
  }

`;



const ProjectOwner = styled.div`
  font-weight: bold;
  text-align: left;
  color: #858585;
  margin-left: 10px;
`;

// const ProjectLikes = styled.div`
//   position: absolute;
//   border: 1px solid #ddd;
//   border-radius: 15px;
//   padding: 2px 8px;
//   color: white;
//   font-weight: bold;
//   background-color: #C4C4C4;
//   transform: translate(300px,-25px);
// `;

const ProjectTitle = styled.h3`
  font-size: 20px;
  margin-top: 20px;
  margin-bottom: 30px;
`;

const Content = styled.div`
  font-size: 12px;
  margin-bottom: 10px;
  min-height: 50px;
  max-height: 100px;
  overflow: hidden;
  color: #858585;
`;

const ProjectInfo = styled.div`
  font-size: 14px;
  color: #777;
`;

const Tags = styled.div`
  display: flex;
  padding-top: 10px;
  margin-bottom: 5px;
  align-items: space-between;
`;

const Tag = styled.div`
  margin-bottom: 10px;
  margin-left: 5px;
  margin-right: 5px;
  border: 1px solid #ddd;
  padding: 5px 10px;
  border-radius: 14px 14px 1px 14px; 
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: white;
  border-color: rgba(160, 218, 251);
  color: #0A8ED9;
`;

const Details = styled.div`
  text-align: left;
  margin-bottom: 5px;
`;

const ApplyButton = styled.button`
  margin-top: 5px;
  margin-left: 5px;
  border: 1px solid #ddd;
  border-radius: 15px;
  padding: 5px;
  width: calc(100% / 4);
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: #62B9EC;
  color: white;
  font-weight: bold;
  cursor: pointer;
  transform: translateX(130px);
  border-radius: 14px 14px 1px 14px; 

  &:hover {
    background-color: #A0DAFB;
  }
`;

const LikeButtonWrapper = styled.div`
  position: absolute;
  top: 36px;
  right: 30px;

  @media (max-width: 1200px) {

    transform: scale(0.9);

  @media (max-width: 768px) {

    transform: scale(0.8); 
  }

  @media (max-width: 480px) {

    transform: scale(0.7);
  }
`;

export default SearchFeed;
