import React, { useState } from 'react';
import styled from 'styled-components';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // 로그인 처리 로직 추가
    console.log('이메일:', email);
    console.log('비밀번호:', password);
    console.log('ID 저장:', rememberMe);
  };

  return (
    <Container>
      <Logo>iTeaMoa</Logo>
      <Form onSubmit={handleSubmit}>
        <Label>이 메 일</Label>
        <Input 
          type="email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          placeholder="이메일 입력" 
          required 
        />
        <Label>비밀번호</Label>
        <Input 
          type="password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          placeholder="비밀번호 입력" 
          required 
        />
        <HelperText>영문 대/소문자와 숫자, 특수문자를 조합하여 12~18자내</HelperText>
        <CheckboxContainer>
          <Checkbox 
            type="checkbox" 
            checked={rememberMe} 
            onChange={() => setRememberMe(!rememberMe)} 
          />
          <CheckboxLabel>ID 저장</CheckboxLabel>
        </CheckboxContainer>
        <Button type="submit">Sign In</Button>
        <SocialLoginContainer>
          <SocialButton yellow>카카오 로그인</SocialButton>
          <SocialButton green>네이버 로그인</SocialButton>
        </SocialLoginContainer>
      </Form>
    </Container>
  );
};

export default LoginPage;

// Styled Components
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #f9f9f9;
`;

const Logo = styled.h1`
  font-size: 36px;
  margin-bottom: 20px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  width: 300px;
`;

const Label = styled.label`
  margin: 10px 0 5px;
  font-size: 14px;
`;

const Input = styled.input`
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  margin-bottom: 15px;
`;

const HelperText = styled.span`
  font-size: 12px;
  color: #888;
  margin-bottom: 10px;
`;

const CheckboxContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 15px;
`;

const Checkbox = styled.input`
  margin-right: 5px;
`;

const CheckboxLabel = styled.label`
  font-size: 14px;
`;

const Button = styled.button`
  padding: 10px;
  background-color: #0080ff;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-bottom: 10px;
`;

const SocialLoginContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const SocialButton = styled.button`
  padding: 10px;
  background-color: ${props => (props.yellow ? '#f7e02e' : '#3dbd4e')};
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-top: 5px;
`;
