import React, { useState } from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown } from '@fortawesome/free-solid-svg-icons';

const TagSelector = ({ options, onTagSelect, selectedTags  }) => {
    const [selectedOptions, setSelectedOptions] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    const handleTagClick = (option) => {
        if (selectedTags.includes(option.label)) return; // 이미 선택된 태그는 클릭 불가능

        if (selectedTags.length < 8) {
            onTagSelect(option); // 선택된 태그 전달
        }
    };

    // const handleTagClick = (option) => {
    //     if (selectedOptions.includes(option.label)) {
    //         setSelectedOptions(selectedOptions.filter(selected => selected !== option.label));
    //     } else {
    //         if (selectedOptions.length < 8) {
    //             setSelectedOptions([...selectedOptions, option.label]);
    //             onTagSelect(option); // 선택된 태그 전달
    //         }
    //     }
    // };

    // const handleTagClick = (option) => {
    //     if (selectedTags.includes(option.label)) return; // 이미 선택된 태그는 클릭 불가능
    //     // ...
    // };

    const filteredOptions = options.filter(option =>
        option.label.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <DropdownWrapper>
            <DropdownHeader>
                <SearchInput
                    type="text"
                    placeholder={selectedOptions.length > 0 ? selectedOptions.join(', ') : '태그를 선택하세요'}
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    onFocus={() => setSearchTerm(searchTerm)} // 포커스 시 검색어 유지
                />

            </DropdownHeader>
            <TagContainer>
                {filteredOptions.length > 0 ? (
                    filteredOptions.map(option => (
                        <Tag
                            key={option.value}
                            onClick={() => handleTagClick(option)}
                            // isSelected={selectedOptions.includes(option.label)}
                            isSelected={selectedTags.includes(option.label)} // 선택된 태그 여부 확인
                            disabled={selectedTags.includes(option.label)} // 클릭 불가능 상태
                        >
                            <TagIcon>{/* 아이콘 추가 가능 */}</TagIcon>
                            {option.label}
                        </Tag>
                    ))
                ) : (
                    <NoOptions>옵션이 없습니다.</NoOptions>
                )}
            </TagContainer>
        </DropdownWrapper>
    );
};

const DropdownWrapper = styled.div`
    position: relative;
    width: 620px;
    margin-top: 10px;

`;

const DropdownHeader = styled.div`
    border: 2px solid #A0DAFB;
    border-radius: 15px;
    padding: 8px;   
    background-color: #fff;
    display: flex;
    width: 400px;
    // align-items: center;
    // justify-content: space-between;
    margin-left:80px;
`;

const SearchInput = styled.input`
    padding: 4px;
    border: none;
    width: 100%;
    box-sizing: border-box;
    outline: none;

    &::placeholder {
        color: grey;
    }
`;

const TagContainer = styled.div`
    display: flex;
    flex-wrap: wrap; 
    max-height: 240px;
    overflow-y: auto;
    // border: 2px solid #A0DAFB;
    border-radius: 15px;
    background: white;
    z-index: 1000;
    margin-top: 3px;
    padding: 10px; /* 패딩 추가 */
`;

const Tag = styled.div`
    background-color: ${({ isSelected }) => (isSelected ? '#A0DAFB' : 'white')};
    border: 2px solid #A0DAFB;
    border-radius: 15px;
    padding: 8px 12px;
    cursor: pointer;
    display: flex;
    align-items: center;
    margin: 5px; /* 태그 간의 간격 추가 */

    &:hover {
        background-color: rgba(160, 218, 251, 0.5);
    }
`;

const TagIcon = styled.span`
    margin-right: 5px; /* 아이콘과 태그 텍스트 간의 간격 */
`;

const NoOptions = styled.div`
    padding: 10px;
    color: #aaa;
`;

export default TagSelector;
