import React, { useState } from 'react';
import styled from 'styled-components';
import {useLocation, useNavigate } from 'react-router-dom';
import {getAuth, OAuthProvider , signInWithPopup} from 'firebase/auth';
import auth from '../../firebase'; // 경로를 수정



const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // 로그인 처리 로직 추가
    console.log('이메일:', email);
    console.log('비밀번호:', password);
    console.log('ID 저장:', rememberMe);
  };


  const handleKakaoLogin = async () => {
    const provider = new OAuthProvider('kakao.com');

    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;
      console.log('로그인 성공:', user);
    } catch (error) {
      console.error('로그인 실패:', error);
    }
  };

  const handleAddButtonClick = () => {
    navigate('/SignupPage'); // SearchPage로 이동
  };


  return (
    <Container>


      <Logo>
      <img
          alt="Logo"
          src="/images/logo1.png"
        onClick={() => (window.location.href = "/")}/>
      </Logo>



      <Form onSubmit={handleSubmit}>
        {/* <Label>이 메 일</Label> */}
        <Con1>
        <Input 
          type="email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          placeholder="이메일 입력" 
          required 
        />
        {/* <Label>비밀번호</Label> */}
        <Input 
          type="password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          placeholder="비밀번호 입력" 
          required 
        />
        <HelperText>영문 대/소문자와 숫자, 특수문자를 조합하여 12~18자내</HelperText>
        <CheckboxContainer>
          <Checkbox 
            type="checkbox" 
            checked={rememberMe} 
            onChange={() => setRememberMe(!rememberMe)} 
          />
          <CheckboxLabel>ID 저장</CheckboxLabel>
        </CheckboxContainer>
        <Button type="submit">로그인</Button>
        </Con1>

        <SocialLoginContainer>
        <SocialButton1 onClick ={handleKakaoLogin} yellow>
            <Icon src="/images/pngwing.com.png" alt="카카오톡 아이콘" /> 카카오계정으로 로그인하기
          </SocialButton1>
          <SocialButton2 >
            <Icon src="/images/네이버 로고 아이콘.png" alt="네이버 아이콘" /> 네이버 아이디로 로그인하기
          </SocialButton2>
          {/* <SocialButton green>네이버 로그인</SocialButton> */}
        </SocialLoginContainer>
      </Form>

      <Signup>
        아직 회원이 아니신가요? <span onClick={handleAddButtonClick}> 회원가입하기 </span>
      </Signup>
    </Container>
  );
};

export default LoginPage;

// Styled Components
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #f9f9f9;
`;

const Logo = styled.div`
  position: absolute;
  top:2%;

  img {
    width: 90%; /* 부모 요소의 너비에 맞게 조정 */
    max-width: 300px; /* 최대 너비 설정 */
    height: auto; /* 비율 유지 */
    cursor: pointer;
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
 align-item:center;
 justify-content: center;
  width: 25%;
`;

const Con1 = styled.form`
  display: flex;
  flex-direction: column;
  border: 3px solid;
  border-color: rgba(160, 218, 251, 0.5);
  width: 90%;
  padding: 20px;
  border-radius: 15px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);

`;

// const Label = styled.label`
//   margin: 10px 0 5px;
//   font-size: 16px;
//   font-weight: bold;
// `;

const Input = styled.input`
  padding: 16px;
  border: 2px solid #0080ff; //진한 파랑
  border-radius: 15px;
  margin-bottom: 10px;
  border-color: rgba(160, 218, 251, 0.5); // 연한 파랑 (이게 적용됨)

`;

const HelperText = styled.span`
  font-size: 12px;
  color: #888;
  margin-bottom: 10px;
`;

const CheckboxContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 15px;
`;

const Checkbox = styled.input`
  margin-right: 5px;

`;

const CheckboxLabel = styled.label`
  font-size: 14px;
`;

const Button = styled.button`
  padding: 10px;
  background-color: #3563E9 ;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-bottom: 10px;
  font-size:14px;
  font-weight: bold;
  padding:14px;
  border-radius: 10px;


  &:hover {
    color: #aaa;

`;

const SocialLoginContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-top:40px;
`;

const SocialButton1 = styled.button`
  padding: 10px;
  background-color: ${props => (props.yellow ? '#f7e02e' : '#3dbd4e')};
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-top: 5px;
  font-size:14px;
  color: black;
  font-weight: bold;
  padding:10px;
  border-radius: 10px;


  &:hover {
    color: #aaa;
`;


const SocialButton2 = styled.button`
  padding: 10px;
  background-color: #03CF5D; /* 네이버 색상 */
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-top: 5px;
  font-size:14px;
  color: white;
  font-weight: bold;
  padding:10px;
  border-radius: 10px;


  &:hover {
    color: #aaa;
`;

const Icon = styled.img`
  width: 25px; /* 아이콘 크기 조절 */
  height: 25px;
  margin-right: 5px;
  vertical-align: middle;

`;


const Signup = styled.span`
  font-size: 14px;
  color: #888;
  margin-top: 20px;

  span {
   color: #0080ff;
   cursor: pointer;
   font-weight: bold;


   &:hover {
    color: #62B9EC;
  }

`;
