//사용자 좋아요 ui와 이벤트 처리


import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart as regularHeart } from '@fortawesome/free-regular-svg-icons'; 
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import styled from 'styled-components';
import axios from 'axios';
// import axios from '../api/axios'


const LikeButton = ({ initialLiked, initialLikesCount, onLikeChange, buttonStyle, apiEndpoint, userId,sk,isApplyPage}) => {
  const [liked, setLiked] = useState(initialLiked);
  const [likesCount, setLikesCount] = useState(initialLikesCount);
  // const { user } = useAuth(); // 로그인한 사용자 정보 가져오기



  // props가 변경될 때 상태 업데이트
  useEffect(() => {
    console.log('Initial liked state:', initialLiked); // 초기 상태 확인
    setLiked(initialLiked);
    setLikesCount(initialLikesCount);
  }, [initialLiked, initialLikesCount]);

  // 추가: 컴포넌트가 마운트될 때 liked 상태를 초기화
  useEffect(() => {
    setLiked(initialLiked);
  }, [initialLiked]);

  useEffect(() => {
    // 로컬 스토리지에서 상태 불러오기
    const storedLiked = localStorage.getItem(`liked_${userId}`);
    const storedLikesCount = localStorage.getItem(`likesCount_${userId}`);

    if (storedLiked !== null) {
      setLiked(JSON.parse(storedLiked));
    } else {
      setLiked(initialLiked);
    }

    if (storedLikesCount !== null) {
      setLikesCount(parseInt(storedLikesCount, 10));
    } else {
      setLikesCount(initialLikesCount);
    }
  }, [initialLiked, initialLikesCount]);

  const handleClick = async (e) => {
    e.stopPropagation(); // 부모 클릭 이벤트 전파 방지
    const newLiked = !liked;
    const newLikesCount = newLiked ? likesCount + 1 : likesCount - 1;

    console.log('Liked state before update:', liked); // 상태 확인
    setLiked(newLiked);
    console.log('Liked state after update:', newLiked); // 상태 확인
    setLikesCount(newLikesCount);
    if (onLikeChange) {
      onLikeChange(newLiked, newLikesCount);
    }

    // API 호출
    const likeData = {
      pk: userId,
      sk: sk,
      feedType: "PROJECT" // 컴포넌트 내부에서 likeData 정의
    };

    console.log('Like data being sent:', likeData); // 요청 데이터 확인

    try {
      if (newLiked) {
        // 좋아요 추가
        const response = await axios.post(apiEndpoint, likeData);
        console.log('Response:', response.data); // 응답 데이터 확인
      } else {
        // 좋아요 제거
        await axios.delete(apiEndpoint, {
          data: likeData // 요청 본문에 데이터 포함
        });
        console.log('Like removed successfully'); // 성공 로그
      }
    } catch (error) {
      console.error('Error updating like status:', error);
      // 상태를 원래대로 되돌리기
      setLiked(!newLiked); // 이전 상태로 되돌림
      setLikesCount(newLikesCount - (newLiked ? 1 : -1)); // 이전 좋아요 수 조정
    }

    // 상태를 로컬 스토리지에 저장
    localStorage.setItem(`liked_${userId}`, newLiked);
    localStorage.setItem(`likesCount_${userId}`, newLikesCount);
  };


  // const handleClick = async (e) => {
  //   e.stopPropagation(); // 부모 클릭 이벤트 전파 방지
  //   const newLiked = !liked;
  //   const newLikesCount = newLiked ? likesCount + 1 : likesCount - 1;

  //   setLiked(newLiked);
  //   setLikesCount(newLikesCount);
  //   if (onLikeChange) {
  //     onLikeChange(newLiked, newLikesCount); // 이벤트 객체 생략
  //   }

  //   // API 호출
  //   const data = {
  //     pk: userId,
  //     sk: sk, // sk 변수가 정의되어야 합니다.
  //     feedType: "PROJECT"
  //   };

  //   try {
  //     if (newLiked) {
  //       // 좋아요 추가
  //       await axios.post(apiEndpoint, data);
  //     } else {
  //       // 좋아요 제거
  //       await axios.delete(apiEndpoint, { data });
  //     }
  //   } catch (error) {
  //     console.error('Error updating like status:', error);
  //     // 상태를 원래대로 되돌리기
  //     setLiked(!newLiked);
  //     setLikesCount(newLikesCount - (newLiked ? 1 : -1));
  //   }
  // };









  return (
    <Button onClick={handleClick} buttonStyle={buttonStyle}>
      <FontAwesomeIcon icon={liked ? faHeart : regularHeart} style={{ color: liked || initialLiked ? 'red' : 'white', marginRight: '4px' }} />
      {likesCount}
    </Button>
  );
};

const Button = styled.div`
  border: 1px solid #ddd;
  padding: 2px 8px;
  // width: 50px;
  border-radius: 15px;
  color: white;
  font-weight: bold;
  background-color: #C4C4C4;
  cursor: pointer;
  float: right;
  margin-top: -24px;

  &:hover {
    background-color: #A0DAFB; 
  }

  ${({ buttonStyle }) => buttonStyle === 's1' && `
    margin-top:0px;

  `}


  ${({ buttonStyle }) => buttonStyle === 'apply' && `
    transform: translate(400px,60px);

  `}
`;

export default LikeButton;
