import React, { useState } from 'react';
import styled from 'styled-components';
import Head from "../../components/Head";
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';


const WritePage = () => {
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [participants, setParticipants] = useState(0);
  const [deadline, setDeadline] = useState('');
  const [progress, setProgress] = useState('');
  const [projectRole, setProjectRole] = useState('');
  const [description, setDescription] = useState('');

  const handleSave = () => {
    // 저장 로직 추가
    console.log({
      title,
      participants,
      deadline,
      progress,
      projectRole,
      description,
    });
    navigate('/'); // 메인 페이지로 이동
  };

  return (
    <>
      <Head />
      <WriteWrapper>
      <BackButton onClick={() => navigate(-1)} style={{ display: 'flex', alignItems: 'center' }}>
      <FontAwesomeIcon icon={faArrowLeft} style={{ marginRight: '5px' }} />
      이전
    </BackButton>
        {/* <BackButton onClick={() => navigate(-1)}>◀ 돌아가기</BackButton> */}

        <Form>


          <TitleInput>
          <InputField
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="제목을 적어주세요"
          />

          </TitleInput>
          </Form>


        <Container>
        <Form> 
        <Title>
          <Label>모집구분 | 프로젝트</Label>
          </Title> 

        <InputBox>



          <InputWrapper>

          <Label>마감일</Label>
          <Input
            type="date"
            value={deadline}
            onChange={(e) => setDeadline(e.target.value)}
          />
          </InputWrapper>

          <InputWrapper>

          <Label>진행장소</Label>
          <Input
            value={progress}
            onChange={(e) => setProgress(e.target.value)}
            placeholder="진행장소"
          />
          </InputWrapper>




          <InputWrapper>   
          <Label>모집 역할</Label>
          <Input
            value={projectRole}
            onChange={(e) => setProjectRole(e.target.value)}
            placeholder="모집 역할"
          />
          </InputWrapper>

          <InputWrapper>   
          <Label> 진행기간 </Label>
          <Input
            value={projectRole}
            onChange={(e) => setProjectRole(e.target.value)}
            placeholder="진행 기간"
          />
          </InputWrapper>
          </InputBox>

        </Form>

        <TagsSection>
          <TagButton>AWS</TagButton>
          <TagButton>Blockchain</TagButton>
          <TagAdd> +추가하기</TagAdd>
          </TagsSection>

        </Container>

        <Body>
        <Form>

          <TextArea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="본문을 작성해주세요"
          />

        </Form>
        </Body>

        <Submit>
        <SaveButton onClick={handleSave}>임시저장</SaveButton>
        <SaveButton onClick={handleSave}>저장하기</SaveButton>
        </Submit>
      </WriteWrapper>
    </>
  );
};

export default WritePage;

const WriteWrapper = styled.div`
  position:relative;
  padding: 20px;
  margin-top: calc(100vh - 45%);
  min-height: calc(100vh - 250px);
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BackButton = styled.button`
//   display: flex;
//   align-items: center;
 position: absolute;
 left: 10%;
 top:5%;
 transform: translateY(30%);
  background: transparent; /* 배경 투명 */
  border: none; /* 박스 선 없음 */
  color: #62b9ec; /* 글씨 파랑 */
  font-size:20px;
  font-weight: bold;
  cursor: pointer; /* 커서 포인터 */

  &:hover {
    opacity: 0.5; /* 호버 시 투명도 조정 */
  }
`;


const Form = styled.div`
//   display: flex;
//   flex-direction: column;
  gap: 10px;
`;

const Label = styled.label`
  font-weight: bold;
  min-width: 70px; /* 라벨의 최소 너비 설정 */
  text-align: left;
`;



const TitleInput = styled.div`
  position: absolute;
  left: 20%;
  top: 5%;
  align-items: center;
  border-bottom: 3px solid #ccc;
  cursor: text;
  width: 60%;

  &:focus-within {
    border-bottom: 3px solid #62b9ec;
