  transform: translateX(16px); /* 오른쪽으로 이동 */
  margin-top:100px;
  padding: 20px;
  border: 2px solid #ddd;
  border-radius: 30px 30px 1px 30px; //반시계 ㅔ방향
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: white;
  border-color: #A0DAFB;
`;


const Title = styled.label`
