import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser as regularUser, faHeart as regularHeart } from '@fortawesome/free-regular-svg-icons'; 
import { faHeart } from '@fortawesome/free-solid-svg-icons';

function Section1({ projects, onLikeClick }) {
  return (
    <SectionWrapper>
      <SectionTitle>인기 프로젝트</SectionTitle>
      <ProjectList>
        {projects.slice(0, 3).map((project, index) => (
          <ProjectCard key={index}>
            <ProjectOwner>
              <FontAwesomeIcon icon={regularUser} style={{ fontSize: '20px', lineHeight: '1.2', marginRight: '6px' }} />
              {project.creatorID}
            </ProjectOwner>
            <ProjectLikes onClick={() => onLikeClick(index)}>
              <FontAwesomeIcon icon={project.liked ? faHeart : regularHeart} style={{ color: project.liked ? 'red' : 'white', marginRight: '4px' }} />
              {project.likesCount}
            </ProjectLikes>
            <ProjectTitle>{project.title}</ProjectTitle>
          </ProjectCard>
        ))}
      </ProjectList>
    </SectionWrapper>
  );
}

const SectionWrapper = styled.div`
  width: calc(100% / 2 + 100px);
  border: 2px solid #A0DAFB;
  border-radius: 30px 30px 1px 30px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: rgba(160, 218, 251, 0.5);
`;

const SectionTitle = styled.h2`
  font-size: 24px;
  margin-bottom: 20px;
`;

const ProjectList = styled.div`
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  gap: 20px;
`;

const ProjectCard = styled.div`
  position: relative;
  border: 2px solid #A0DAFB;
  border-radius: 30px;
  padding: 15px;
  margin-bottom: 20px;
  width: calc(100% / 4);
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: white;
  cursor: pointer;

  &:hover {
    background-color: #A0DAFB;
  }
`;

const ProjectOwner = styled.div`
  font-weight: bold;
  position: absolute;
  color: #858585;
`;

const ProjectLikes = styled.div`
  border: 1px solid #ddd;
  padding: 2px 8px;
  border-radius: 15px;
  color: white;
  font-weight: bold;
  right: 10%;
  position: absolute;
  background-color: #C4C4C4;
  cursor: pointer;

  &:hover {
    background-color: #A0DAFB; 
  }
`;

const ProjectTitle = styled.h3`
  font-size: 20px;
  margin: 20px 0;
`;

export default Section1;
