import React, { useState } from 'react';
import styled from 'styled-components';
import ApplyHead from "../../components/ApplyHead";
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';


const ApplyPage = () => {


  const navigate = useNavigate(); 
  const [comments, setComments] = useState([]);
  const [commentInput, setCommentInput] = useState('');

  const handleCommentSubmit = () => {
    if (commentInput.trim()) {
      setComments([...comments, commentInput]);
      setCommentInput('');
    }
  };

  return (

    <>
    <ApplyHead/>
    <Container>
    <BackButton onClick={() => navigate(-1)} style={{ display: 'flex', alignItems: 'center' }}>
      <FontAwesomeIcon icon={faArrowLeft} style={{ marginRight: '5px' }} />
      돌아가기
    </BackButton>

      <Post>
        <PostTitle>재난 대응 어플리케이션 백엔드 구해요!</PostTitle>
        <PostDetails>
          <Detail>모집구분: 프로젝트</Detail>
          <Detail>모집인원: 2/4</Detail>
          <Detail>마감일: 2024.07.29</Detail>
          <Detail>진행기간: 3개월</Detail>
          <Detail>진행장소: 강남역</Detail>
          <Detail>신청자수: 백엔드(3), 디자이너(1)</Detail>
          <Detail>모집 역할: 백엔드(2), 디자이너(1)</Detail>
        </PostDetails>
        <PostDescription>
          블록체인 DApp을 개발할 예정입니다! 아지 어떤 어플리케이션의 아이디어는 미정이며, 프로젝트 팀원이 모이면 같이 브레인스토밍을 통해 결정할 예정입니다.
        </PostDescription>
      </Post>

      <CommentsSection>
        <CommentsTitle>댓글 ({comments.length})</CommentsTitle>
        <CommentsList>
          {comments.map((comment, index) => (
            <Comment key={index}>{comment}</Comment>
          ))}
        </CommentsList>
        <CommentInputWrapper>
          <CommentInput
            value={commentInput}
            onChange={(e) => setCommentInput(e.target.value)}
            placeholder="댓글을 입력해 주세요."
          />
          <CommentButton onClick={handleCommentSubmit}>등록</CommentButton>
        </CommentInputWrapper>
      </CommentsSection>
    </Container>
    </>
  );
};

export default ApplyPage;

const Container = styled.div`
  position: relative;
  padding: 20px;
  margin-top: calc(100vh - 30%);
//   max-width: 800px;
  min-height: calc(100vh - 250px);
  display: flex;
  flex-direction: column;
  align-items: center;

`;

const BackButton = styled.button`
//   display: flex;
//   align-items: center;
 position: absolute;
 left: 10%;
 top:5%;
 transform: translateY(30%);
  background: transparent; /* 배경 투명 */
  border: none; /* 박스 선 없음 */
  color: #62b9ec; /* 글씨 파랑 */
  font-size:20px;
  font-weight: bold;
  cursor: pointer; /* 커서 포인터 */

  &:hover {
    opacity: 0.5; /* 호버 시 투명도 조정 */
  }
`;

const Post = styled.div`
  margin-bottom: 20px;
  padding: 20px;
  border: 1px solid #ddd;
  border-radius: 10px;
  background-color: #fff;
`;

const PostTitle = styled.h2`
  font-size: 24px;
  margin: 0;
`;

const PostDetails = styled.div`
  margin: 10px 0;
`;

const Detail = styled.div`
  font-size: 14px;
`;

const PostDescription = styled.p`
  font-size: 16px;
  margin: 10px 0;
`;

const CommentsSection = styled.div`
  border-top: 1px solid #ddd;
  padding-top: 10px;
`;

const CommentsTitle = styled.h3`
  font-size: 20px;
`;

const CommentsList = styled.div`
  margin: 10px 0;
`;

const Comment = styled.div`
  margin: 5px 0;
  padding: 10px;
  border: 1px solid #e0e0e0;
  border-radius: 5px;
`;

const CommentInputWrapper = styled.div`
  display: flex;
  align-items: center;
`;

const CommentInput = styled.input`
  flex: 1;
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 5px;
  margin-right: 10px;
`;

const CommentButton = styled.button`
  padding: 10px 15px;
  border: none;
  border-radius: 5px;
  background-color: #62b9ec;
  color: white;
  cursor: pointer;

  &:hover {
    background-color: #a0dafb;
  }
`;
