import React, { useEffect, useState } from 'react';
import styled from 'styled-components';


const Head = () => {


    const [visible, setVisible] = useState(true);
    const [lastScrollY, setLastScrollY] = useState(0);
    const [toggleActive, setToggleActive] = useState(false); // ÌÜ†Í∏Ä ÏÉÅÌÉú Ï∂îÍ∞Ä

    const handleScroll = () => {
      if (typeof window !== 'undefined') {
        const currentScrollY = window.scrollY;
        setVisible(lastScrollY > currentScrollY || currentScrollY < 10);
        setLastScrollY(currentScrollY);
      }
    };

    useEffect(() => {
      window.addEventListener('scroll', handleScroll);
      return () => {
        window.removeEventListener('scroll', handleScroll);
      };
    }, [lastScrollY]);

    const handleToggleChange = () => {
      setToggleActive(!toggleActive); // ÌÜ†Í∏Ä ÏÉÅÌÉú Î≥ÄÍ≤Ω
    };



  return (
    <HeadWrapper visible={visible}>
    <Logo onClick={() => (window.location.href = "/")}>
      <span>iTeaMoa</span>
    </Logo>
    <ToggleContainer>
      <ToggleLabel active={toggleActive}>STUDY</ToggleLabel>
      <ToggleSwitch type="checkbox" checked={toggleActive} onChange={handleToggleChange} />
      <ToggleLabel active={!toggleActive}>PROJECT</ToggleLabel>
    </ToggleContainer>


    <UserActions>
        <UserIcon>üîî</UserIcon>
        <UserIcon>üí¨</UserIcon>
        <UserIcon>üë§</UserIcon>
        <LogoutButton>Î°úÍ∑∏ÏïÑÏõÉ</LogoutButton>
    </UserActions>
  </HeadWrapper>
  )
}

export default Head;


const HeadWrapper = styled.nav`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 20%;
  background-color: #ffffff;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 36px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  z-index: 3;
  transition: transform 0.5s ease;
  transform: translateY(${props => (props.visible ? '0' : '-100%')});
`;


const Logo = styled.div`
  position: absolute; 
  top: 20%; 
  cursor: pointer;
  font-size: 40px;
  color: #0080ff; 
`;

const ToggleContainer = styled.div`
  display: flex;
  align-items: center;
  position: absolute;
  top: 35%;
  cursor: pointer;
  font-size: 24px;
`;

const ToggleLabel = styled.label`
  margin: 0 10px;
  font-size: 16px;
  font-weight: bold;
  color: ${props => (props.active ? '#aaa' : '#0080ff')}; // ÌôúÏÑ±Ìôî Ïó¨Î∂ÄÏóê Îî∞Îùº ÏÉâÏÉÅ Î≥ÄÍ≤Ω
`;

const ToggleSwitch = styled.input`
  appearance: none;
  width: 50px;
  height: 30px;
  background: #ccc; 
  border-radius: 30px; 
  position: relative;
  cursor: pointer;
  outline: none;

  &:checked {
    background: #62B9EC; 
  }

  &:checked::before {
    transform: translateX(24px); 
  }

  &::before {
    content: '';
    position: absolute;
    width: 26px; 
    height: 26px; 
    top: 7%;
    border-radius: 50%; 
    background: white; 
    transition: transform 0.2s; 
  }
`;



const UserActions = styled.div`
  position: absolute;
  top: 10%;
  right: 5%;
  display: flex;
  align-items: center;
  color: #0080ff;
`;



const LogoutButton = styled.div`
  border: 1px none #0080ff;
  background-color: #3563E9;  
  margin: 0 10px;
  padding: 8px 16px;
  border-radius: 5px;
  font-size: 16px;
  color: white;
  cursor: pointer;

  &:hover {
    color: #0080ff;
  }
`;


const UserIcon = styled.div`
  margin: 0 10px;
  font-size: 25px;
  cursor: pointer;
`;
