import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom'; // 추가
import apiClient from '../../services/api'; // 추가

const SignUpPage = () => {
  const [email, setEmail] = useState('');
  const [authNumber, setAuthNumber] = useState('');
  const [isAuthCodeValid, setIsAuthCodeValid] = useState(false); // Auth code validation status
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [nickname, setNickname] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  // Email Verification Handler
  const handleEmailVerification = async () => {
    if (!email) {
      setErrorMessage('이메일을 입력해주세요.');
      return;
    }

    try {
      console.log('Email Verification Button Clicked');
      const response = await apiClient.post('/verify/verify-email', { email });
      setSuccessMessage('인증번호가 이메일로 발송되었습니다.');
      console.log('Email verification initiated:', response.data);
    } catch (error) {
      console.error('Email verification failed:', error);
      setErrorMessage(
        error.response?.data?.message || '이메일 인증에 실패했습니다. 다시 시도해주세요.'
      );
    }
  };

  // Auth Code Confirmation Handler
  const handleConfirmAuthCode = async () => {
    if (!authNumber) {
      setErrorMessage('인증번호를 입력해주세요.');
      return;
    }

    try {
      console.log('Confirm Auth Code Button Clicked');
      const response = await apiClient.post('/verify/confirm-email', { email, authNumber });
      setIsAuthCodeValid(true);
      setSuccessMessage('이메일 인증이 완료되었습니다.');
      console.log('Auth code confirmed:', response.data);
    } catch (error) {
      console.error('Auth code confirmation failed:', error);
      setIsAuthCodeValid(false);
      setErrorMessage(
        error.response?.data?.message || '인증번호 확인에 실패했습니다. 다시 시도해주세요.'
      );
    }
  };

  // Signup Submission Handler
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!isAuthCodeValid) {
      setErrorMessage('이메일 인증을 먼저 완료해주세요.');
      return;
    }

    if (password !== confirmPassword) {
      setErrorMessage('비밀번호가 일치하지 않습니다.');
      return;
    }

    try {
      console.log('Signup Submit Button Clicked');
      const response = await apiClient.post('/signup', {
        email,
        authNumber,
        password,
        nickname,
      });

      setSuccessMessage('회원가입이 완료되었습니다.');
      console.log('Signup successful:', response.data);

      // Navigate to Login Page
      navigate('/LoginPage');
    } catch (error) {
      console.error('Signup failed:', error);
      setErrorMessage(
        error.response?.data?.message || '회원가입에 실패했습니다. 다시 시도해주세요.'
      );
    }
  };


};


export default SignUpPage;

