import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import Nav from "../../components/Nav"; 
import SearchFeed from "./SearchFeed"; 
import styled from 'styled-components';
import axios from 'axios';

const SearchPage = () => {
    const [searchResults, setSearchResults] = useState([]);
    const showSearch = true;

    const useQuery = () => {
        return new URLSearchParams(useLocation().search);
    };

    let query = useQuery();
    const searchTerm = query.get("q");
    const tags = query.get("tags");

    useEffect(() => {
        const fetchSearchItems = async () => {
            try {
                const response = await axios.get(`/main/search-keyword?feedType=PROJECT&keyword=${encodeURIComponent(searchTerm)}`);
                const filteredResults = response.data.filter(item => {
                    // 태그가 선택된 경우, 태그 필터링
                    return !tags || (item.tags && item.tags.some(tag => tags.split(',').includes(tag)));
                });
                setSearchResults(filteredResults); // 필터링된 결과 설정
            } catch (error) {
                console.error("Error fetching search items:", error);
            }
        };

        fetchSearchItems();
    }, [searchTerm, tags]); // 검색어와 태그가 변경될 때마다 호출

    return (
        <Container>
            <Nav showSearch={showSearch} />
            {searchResults.length > 0 ? (
                <SearchFeed itemList={searchResults} setSearchResults={setSearchResults} />
            ) : (
                <NoResults>찾고자하는 검색어에 맞는 아이템이 없습니다.</NoResults>
            )}
        </Container>
    );
};

export default SearchPage;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
`;

const NoResults = styled.p`
  font-size: 24px;
  font-weight: bold;
  color: #777;
`;
