// Section2.js
import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom'; // useNavigate 훅 import
import items from '../data.js'; // 임의 데이터 파일 import
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {  faUser as regularUser } from '@fortawesome/free-regular-svg-icons'; 
import { faHeart } from '@fortawesome/free-solid-svg-icons';

const Section2 = ({ onApplyClick }) => {
  const [allProjects, setAllProjects] = useState([]);
  const navigate = useNavigate(); // useNavigate 훅을 사용하여 navigate 함수 생성

  const fetchAllProjects = () => {
    // Instead of API, use data.js (for practice)
    setAllProjects(items);
    console.log("Loaded projects:", items); // 데이터 확인
  };

  useEffect(() => {
    fetchAllProjects();
  }, []);



  // const handleProjectClick = (project) => {
  //   // 클릭된 프로젝트의 ID를 사용하여 상세 페이지로 이동
  //   // navigate(`/projects/${project.Id}`); // 경로는 필요에 따라 수정하세요
  //   // navigate('/ApplyPage');
  //   navigate(`/ApplyPage/${project.pk}`); // 프로젝트의 pk를 전달
  // };

  const handleProjectClick = (project) => {
    const modifiedPk = project.pk.replace('#', ''); // pk에서 # 제거/ React Router의 useParams는 해시를 처리하지 않음
    navigate(`/ApplyPage/${modifiedPk}`); // 수정된 pk로 상세 페이지로 이동
  };



  return (
    <SectionWrapper>
      <SectionTitle>프로젝트 목록</SectionTitle>
      <ProjectList>
        {allProjects.slice(0, 4).map((project, index) => (
          /* allProjects 배열의 첫4개의 오소 순회 /  slice(0,4) : 배열의 이브븐 추출하여 새로운 배열 생성 */
          /* 각 프로젝트 객체를 project 라는 변수로 받고 그 인덱스를 index라는 변수로 받는다  */
          <ProjectCard key={index}>
            <ProjectOwner><FontAwesomeIcon icon={regularUser} style={{ fontSize: '20px', lineHeight: '1.2', marginRight: '6px' }} />{project.creatorid}</ProjectOwner>
            <ProjectLikes>
              <FontAwesomeIcon icon={faHeart} style={{ color: 'red', marginRight: '4px' }} />
              {project.likescount}
            </ProjectLikes>
            <ProjectTitle>{project.title}</ProjectTitle>
            <Content>{project.content}</Content>
            <ProjectInfo>
              <Tags>
                {project.tags.map((tag, tagIndex) => (
                  <Tag key={tagIndex}>{tag}</Tag>
                ))}
              </Tags>
                <Details>
                모집인원 | {project.recruitmentnum}명
                </Details>

                <Details>
                 마감일 |{new Date(project.deadline).toLocaleDateString()}
                 </Details>

            </ProjectInfo>
            <ApplyButton onClick={() => handleProjectClick(project)}>신청하기</ApplyButton>
          </ProjectCard>
        ))}
      </ProjectList>
    </SectionWrapper>
  );
};

// 스타일 컴포넌트
const SectionWrapper = styled.div`
  display: flex;
  flex-direction: column; /* 세로 방향으로 정렬 */
  align-items: center; /* 가로 방향 중앙 정렬 */
  width: 100%; /* 전체 너비 사용 */
  margin-top: 50px;
  margin-bottom: 40px;
`;


const SectionTitle = styled.h2`
  font-size: 24px;
  margin-bottom: 20px;


`;


// const ProjectList = styled.div`

//   display: grid; /* Grid 사용 */
//   grid-template-columns: 350px 350px; /* 두 개의 카드가 동일한 너비를 가지도록 설정 */
//   gap: 0px 60px; /* 수직 간격 50px, 수평 간격 20px */
// `;


const ProjectList = styled.div`
  display: flex; /* Flexbox 사용 */
  flex-direction: column;
  flex-wrap: wrap; /* 카드가 다음 줄로 넘어가도록 설정 */
  justify-content: center; /* 카드들을 중앙 정렬 */
  // gap: 20px; /* 카드 간의 간격 설정 */
  width: 60%;
  max-height: 50vw;
`;



// const ProjectList = styled.div`
//   // flex-wrap: wrap; /* 카드가 여러 줄로 나올 수 있도록 설정 */
//   // justify-content: center; /* 카드들을 중앙 정렬 */
//   // gap: 20px; /* 카드 간의 간격 설정 */

//   display: flex; /* Flexbox 사용 */
//   flex-wrap: wrap; /* 카드가 다음 줄로 넘어가도록 설정 */
//   justify-content: space-between; /* 카드 간의 간격을 균등하게 배치 */
//   max-width: 800px; /* 전체 너비 제한 */

// `;






const ProjectCard = styled.div`
 position: relative;
  border: 2px solid #A0DAFB;
  border-radius: 30px 30px 1px 30px;
  padding: 15px;
  margin-bottom: 20px;
  margin-top: 20px;
  width: 350px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: white;
  min-height: 200px; /* 최소 높이 설정 */
  max-height: 400px; 
`;

//grid 사용
// const ProjectCard = styled.div`
//   position: relative;
//   border: 2px solid #A0DAFB;
//   border-radius: 30px 30px 1px 30px;
//   padding: 15px;
//   margin-bottom: 20px;
//   width: 350px;
//   margin-top: 20px;
//   box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
//   background-color: white;
//   // min-height: 200px; /* 최소 높이 설정 */
//   /* max-height: 400px;  필요에 따라 설정 */
// `;

const ProjectOwner = styled.div`
  font-weight: bold;
  text-align: left;

`;

const ProjectLikes = styled.div`
  position: absolute;
  border: 1px solid #ddd;
  border-radius: 15px;
  padding: 2px 8px;
  color: white;
  font-weight: bold;
  background-color: #C4C4C4;
  transform: translate(300px,-25px);

`;

const ProjectTitle = styled.h3`
  font-size: 20px;
  margin-top:20px;
  margin-bottom: 30px;
`;

// const Content = styled.div`
//   font-size: 12px;
//   margin-bottom: 10px;
//   min-height: 50px;
// `;

const Content = styled.div`
  font-size: 12px;
  margin-bottom: 10px;
  min-height: 50px; /* 최소 높이 설정 */
  max-height: 100px; /* 필요에 따라 설정 */
  overflow: hidden; /* 내용이 많을 경우 넘치는 부분 숨기기 */
  color: #858585;
`;

const ProjectInfo = styled.div`
  font-size: 14px;
  color: #777;
`;

const Tags = styled.div`
  display: flex;
  padding-top: 10px;
  margin-bottom: 5px;
  align-items: space-between;
`;

const Tag = styled.div`
  margin-bottom: 10px;
  margin-left: 5px;
  margin-right:  5px;
  border: 1px solid #ddd;
  padding: 5px 10px;
  border-radius: 14px 14px 1px 14px; 
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: white;
  border-color: rgba(160, 218, 251);
  color: #0A8ED9;
`;

const Details = styled.div`
 text-align: left;
 margin-bottom: 5px;

`;

const ApplyButton = styled.button`
  margin-top: 5px;
  margin-left: 5px;
  border: 1px solid #ddd;
  border-radius: 15px;
  padding: 5px;
  width: calc(100% / 4);
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: #62B9EC;
  color: white;
  font-weight: bold;
  cursor: pointer;
  transform: translateX(130px);
  border-radius: 14px 14px 1px 14px; 

  &:hover {
    background-color: #A0DAFB;
  }
`;

export default Section2;
