import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faComment } from '@fortawesome/free-solid-svg-icons';

const ProjectDetail = ({ project,onBack }) => {
    const applicants = project.applicants || []; // 기본값 설정

    return (
        <DetailContainer>
            <LeftSection>
                <DetailHeader>
                    <h2>{project.title}</h2>

                </DetailHeader>
                <DetailInfo>


                    <InfoItem>모집 구분 | 프로젝트</InfoItem>
                    <InfoItem>게시 일자 | {new Date(project.timestamp).toLocaleDateString()}</InfoItem>
                    <InfoItem>마감 일자  | {new Date(project.deadline).toLocaleDateString()}</InfoItem>
                    <InfoItem>진행 장소 | {project.place}</InfoItem>
                    <InfoItem>모집 현황 | {project.applyNum}명 / {project.recruitmentNum}명</InfoItem>
                    <InfoItem>진행 기간 | {project.period}개월</InfoItem>
                    <InfoItem>모집 역할 | 백엔드(2), 디자이너(1)</InfoItem>
                    <InfoItem>신청자 수 | 백엔드(3), 디자이너(1)</InfoItem>

                    <Tags>
                        {project.tags.map((tag, index) => (
                            <Tag key={index}>{tag}</Tag>
                        ))}
                    </Tags>
                </DetailInfo>
                <BackButton onClick={onBack}>목록</BackButton>
                <Button>모집완료</Button>
            </LeftSection>
            <RightSection>
                <ApplicationStatus>
                    <h3>신청현황</h3>
                    {applicants.length > 0 ? (
                        applicants.map((applicant, index) => (
                            <Applicant key={index}>
                                 <FontAwesomeIcon icon={faComment} style={{ color: '#62b9ec' , fontSize: '24px'}} />
                                <ApplicantName>{applicant.name}</ApplicantName>
                                <StatusButton status={applicant.status}>
                                    {applicant.status}
                                </StatusButton>
                            </Applicant>
                        ))
                    ) : (
                        <p>신청자가 없습니다.</p>
                    )}
                </ApplicationStatus>
            </RightSection>
        </DetailContainer>
    );
};


const DetailContainer = styled.div`
    display: flex;
    justify-content: space-between; /* 두 섹션을 양쪽으로 배치 */
    margin-top: 60px;
`;

const LeftSection = styled.div`
    flex: 1; /* 왼쪽 섹션이 가능한 모든 공간을 차지 */
    margin-right: 20px; 
    max-height: 400px;
    border: 2px solid #A0DAFB;
    border-radius: 20px;
    padding: 40px;
    background: white;
    padding-bottom:20px;
    padding-top:20px;
`;

const RightSection = styled.div`
    flex: 0 0 300px; /* 오른쪽 섹션의 고정 너비 설정 */
    min-width: 600px;
    min-height:600px;
    border: 2px solid #A0DAFB;
    border-radius: 20px;
    padding: 20px;
    background: white;

`;

const DetailHeader = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
`;

const DetailInfo = styled.div`
    margin: 20px 0;
    display: grid;
    text-align: left;
    grid-template-columns: repeat(2, 1fr); /* 2 열로 배치 */
    grid-gap: 5px;
`;

const InfoItem = styled.p`
    margin: 5px 0;
`;

const Tags = styled.div`
    margin-top:20px;
    display: flex;
    flex-wrap: wrap;

`;

const Tag = styled.span`
    padding: 5px 10px;
    margin: 5px;
    font-size: 14px;
    border: 1px solid ;
    border-radius: 14px 14px 1px 14px; //반시계 ㅔ방향
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
    border-color: rgba(160, 218, 251);
    background-color: white;
    color: #0A8ED9 ;
`;

const ApplicationStatus = styled.div`
    margin-top: 20px;
    text-align: left;

    h3{
    margin-top: 20px;
    font-size: 24px;
    }
`;

const Applicant = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin: 10px 0;
    padding: 10px;
    border-bottom: 2px solid #A0DAFB;
    border-radius: 1px;
    text-align: left;
`;

const ApplicantName = styled.span`
    font-weight: bold;
    text-align: left;
`;

const StatusButton = styled.button`
    background: ${({ status }) => (status === "승인" ? "#4caf50" : "#f44336")};
    color: white;
    border: none;
    padding: 5px 10px;
    border-radius: 5px;
`;

const Button = styled.button`
    background-color: #3563E9;
    color: white;
    border: none;
    padding: 10px 20px;
    margin-top: 20px; 
    border-radius: 5px;
    cursor: pointer;
    float: right;
`;

const BackButton = styled.button`
    background: rgba(170, 168, 168, 0.69);
    color: white;
    border: none;
    padding: 10px 30px;
    border-radius: 5px;
    cursor: pointer;
    margin-top: 20px; 
    float:left;
`;


export default ProjectDetail;
