  const [commentInput, setCommentInput] = useState('');
  const [project, setProject] = useState(null);
  const [liked, setLiked] = useState(false);





//암의의 데이터 파일
// const fetchProjectDetails = () => {
//   // projectId를 'FEED#5678' 형태로 변환
//   const formattedProjectId = projectId.slice(0, 4) + '#' + projectId.slice(4);

//   console.log("Formatted Project ID:", formattedProjectId); // 변환된 projectId 확인
//   const selectedProject = items.find(item => item.pk === formattedProjectId); // pk와 비교

//   console.log("Selected Project:", selectedProject); // 찾은 프로젝트
//   setProject(selectedProject);
// };


  const fetchProjectDetails = async () => {
    try {
      // projectId를 'FEED#5678' 형태로 변환
      const formattedProjectId = projectId.slice(0, 4) + '#' + projectId.slice(4);

      // 데이터 가져오기
      const response = await axios.get('/data.json');

      // 가져온 데이터에서 특정 프로젝트 찾기
      const selectedProject = response.data.find(item => item.pk === formattedProjectId);
      // 상태 업데이트
      setProject(selectedProject);
    } catch (error) {
      console.error("Error fetching project details:", error);
    }
  };



  // const fetchProjectDetails = () => {

  //   console.log("Project ID from URL:", projectId); // URL에서 가져온 projectId
  //   const selectedProject = items.find(item => item.pk === projectId);

  //   console.log("Selected Project:", selectedProject); // 찾은 프로젝트
  //   setProject(selectedProject);
  // } //selectedProject 변수에 items 배열에서 pk와 projectId가 일치하는 첫번째 프로젝트가 저장됨
  // //find() 메서드는 배열에서 조건을 만족하는 첫번쨰 요소(items의 [0]) 반환 
  // //item : items 배열의 각 요소를 나타냄( 화살표 함수의 매개변수)

  useEffect(() => {
    fetchProjectDetails();
  }, [projectId])
  //useEffect : 컴포넌트가 렌더링 된 후에 사이드 이펙트 수행을 위함 . 데이터 fecthing등에 사용됨 
  //projecId가 변경될 때 마다 실행
  //첫번째 인자: 실행할 함수 , 두번째 인재 의존성 배열



  const handleLike = () => {
    // 좋아요 상태 토글
    setLiked(prevLiked => {
      const newLikedState = !prevLiked;

      // likescount 업데이트
      if (project) {
        setProject(prevProject => ({
          ...prevProject,
          likescount: newLikedState ? prevProject.likescount + 1 : prevProject.likescount - 1
        }));
      }

      return newLikedState; // 새로운 좋아요 상태 반환
    });
  };


  // const handleCommentSubmit = () => {
  //   if (commentInput.trim()) {
  //     setComments([...comments, commentInput]);
  //     setCommentInput('');
  //   }
  // };

  const handleCommentSubmit = () => {
    if (commentInput.trim() && project) {
      const newComment = {
        userid: "USER#1234",  // 현재 사용자 ID
        comment: commentInput,
        timestamp: new Date().toISOString()  // 현재 시간
      };

      // 기존 댓글 배열에 새로운 댓글 추가
      setProject(prevProject => ({
        ...prevProject,
        comments: [...prevProject.comments, newComment]  // 댓글 추가
      }));

      setCommentInput('');  // 입력 필드 초기화
    }
  };


    // 프로젝트가 로딩 중일 때 처리
    if (!project) {
      return <Container>Project ID:{projectId}</Container>; // 로딩 중 메시지
    }



  return (
    <>
      <ApplyHead/>
      <h1>Project ID: {projectId}</h1>
      <Container>
        <BackButton onClick={() => navigate(-1)} style={{ display: 'flex', alignItems: 'center' }}>
          <FontAwesomeIcon icon={faArrowLeft} style={{ marginRight: '5px' }} />
          이전
        </BackButton>

        <Title> {project.title} </Title>

        <LikeButton onClick={handleLike}>
          <FontAwesomeIcon icon={liked ? faHeart : regularHeart} style={{ color: liked ? 'red' : 'white', marginRight: '4px' }} />
          {project.likescount}
        </LikeButton>

        <Post>
          <PostDetails>
            <Detail>모집구분 | 프로젝트</Detail>
            <Detail>마감일 | {new Date(project.deadline).toLocaleDateString()}</Detail>
            <Detail>모집현황 | {project.recruitmentnum}명 </Detail>
            <Detail>게시일자 |  {new Date(project.timestamp).toLocaleDateString()}</Detail>
            <Detail>진행기간 | {project.period}개월</Detail>
            <Detail>진행장소 | {project.place}</Detail>
            <Detail>신청자수 | 백엔드(3), 디자이너(1)</Detail>
            <Detail>모집 역할 | 백엔드(2), 디자이너(1)</Detail>
          </PostDetails>

          <TagsSection>
          {project.tags.map((tag, index) => (
              <TagButton key={index}>{tag}</TagButton>
            ))}
          </TagsSection>


          <ApplyButton>신청하기</ApplyButton>


        </Post>

        <PostDescription>

        {project.content}

        </PostDescription>


        <AuthorSection>
          <ChatButton>
            <FontAwesomeIcon icon={faComment} />
            채팅
          </ChatButton>

          <AuthorID> <FontAwesomeIcon icon={faUser} style={{ fontSize: '20px', lineHeight: '1.2', marginRight: '6px' }} /> 작성자: {project.creatorid}</AuthorID>
          </AuthorSection>


        <CommentsSection>
          <CommentsTitle>댓글 ({project.comments.length})</CommentsTitle>

           {/* <CommentsList>

            {comments.map((comment, index) => (
              <Comment key={index}>{comment}</Comment>
            ))} 
          </CommentsList>  */}

      {/* <CommentsList>
            {comments.map((comment, index) => (
              <Comment key={index}>
                <strong>{comment.userid}: </strong>{comment.comment}
                <span style={{ fontSize: 'small', color: '#aaa' }}>
                  {new Date(comment.timestamp).toLocaleString()}
                </span>
              </Comment>
            ))}
          </CommentsList> */}
           <CommentsList>
            {project.comments.map((comment, index) => (
              <Comment key={index}>
                <strong >{comment.userid}: </strong>{comment.comment}
                <span style={{ fontSize: 'small', color: '#aaa' }}>
                {new Date(comment.timestamp).toLocaleDateString()} {new Date(comment.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                </span>
              </Comment>
            ))}
          </CommentsList>
           <CommentInputWrapper>
            <CommentInput

              value={commentInput}
              onChange={(e) => setCommentInput(e.target.value)}
              placeholder="댓글을 입력해 주세요."
            />
              <CommentButton onClick={handleCommentSubmit}>등록</CommentButton>
              </CommentInputWrapper> 




        </CommentsSection>
      </Container>
    </>
  );
};

