// frontend-service/src/services/auth.js
import jwtDecode from 'jwt-decode';
import axios from 'axios';

// Check if the token is expired
export const isTokenExpired = (token) => {
    if (!token) return true;
    const { exp } = jwtDecode(token);
    const currentTime = Math.floor(Date.now() / 1000);
    return exp < currentTime;
};

// Store tokens securely
export const storeTokens = (accessToken, idToken, refreshToken, rememberMe = false) => {
    if (rememberMe) {
        localStorage.setItem('accessToken', accessToken);
        localStorage.setItem('idToken', idToken);
    } else {
        sessionStorage.setItem('accessToken', accessToken);
        sessionStorage.setItem('idToken', idToken);
    }

    // Backend should handle setting the refreshToken in a HttpOnly cookie
    document.cookie = `refreshToken=${refreshToken}; HttpOnly; Secure; SameSite=Strict`;
};

// Refresh tokens
export const refreshAccessToken = async () => {
    try {
        const response = await axios.post('/auth/refresh', {}, { withCredentials: true });
        const { accessToken, idToken } = response.data;

        // Update tokens in storage
        sessionStorage.setItem('accessToken', accessToken);
        sessionStorage.setItem('idToken', idToken);

        return accessToken;
    } catch (error) {
        console.error('Token refresh failed', error);
        throw error;
    }
};

export const clearTokens = () => {
    sessionStorage.removeItem('accessToken');
    sessionStorage.removeItem('idToken');
    localStorage.removeItem('accessToken');
    localStorage.removeItem('idToken');

    document.cookie = 'refreshToken=; Max-Age=0; Secure; SameSite=Strict';
};
