import React, { useState } from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faChevronUp } from '@fortawesome/free-solid-svg-icons';

const TagSelector = ({ options, onTagSelect }) => {
    const [selectedOptions, setSelectedOptions] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [isOpen, setIsOpen] = useState(false);

    const handleTagClick = (option) => {
        if (selectedOptions.includes(option.label)) {
            setSelectedOptions(selectedOptions.filter(selected => selected !== option.label));
        } else {
            if (selectedOptions.length < 8) {
                setSelectedOptions([...selectedOptions, option.label]);
                onTagSelect(option); // 선택된 태그 전달
            }
        }
    };

    const filteredOptions = options.filter(option =>
        option.label.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <DropdownWrapper>
            <DropdownHeader onClick={() => setIsOpen(prev => !prev)}>
                <SearchInput
                    type="text"
                    placeholder={selectedOptions.length > 0 ? selectedOptions.join(', ') : '태그를 선택하세요'}
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    onFocus={() => setIsOpen(true)}
                />
                <FontAwesomeIcon
                    icon={isOpen ? faChevronUp : faChevronDown}
                    style={{ color: '#A0DAFB', fontSize: "1.2em" }}
                />
            </DropdownHeader>
            {isOpen && (
                <TagContainer>
                    {filteredOptions.length > 0 ? (
                        filteredOptions.map(option => (
                            <Tag
                                key={option.value}
                                onClick={() => handleTagClick(option)}
                                isSelected={selectedOptions.includes(option.label)}
                            >
                                <TagIcon>{/* 아이콘 추가 가능 */}</TagIcon>
                                {option.label}
                            </Tag>
                        ))
                    ) : (
                        <NoOptions>옵션이 없습니다.</NoOptions>
                    )}
                </TagContainer>
            )}
        </DropdownWrapper>
    );
};

const DropdownWrapper = styled.div`
    position: relative;
    width: 300px;
`;

const DropdownHeader = styled.div`
    border: 2px solid #A0DAFB;
    border-radius: 15px;
    padding: 8px;   
    cursor: pointer;
    background-color: #fff;
    display: flex;
    // align-items: center;
    // justify-content: space-between;

`;

const SearchInput = styled.input`
    padding: 4px;
    border: none;
    width: 100%;
    box-sizing: border-box;
    outline: none;

    &::placeholder {
        color: grey;
    }
`;

const TagContainer = styled.div`
    display: flex;
    flex-wrap: wrap;
    gap: 10px;
    position: absolute;
    background: white;
    border: 2px solid #A0DAFB;
    border-radius: 15px;
    width: 100%;
    max-height: 250px;
    overflow-y: auto;
    z-index: 1000;
    margin-top: 3px;
`;

const Tag = styled.div`
    background-color: ${({ isSelected }) => (isSelected ? '#A0DAFB' : 'white')};
    border: 2px solid #A0DAFB;
    border-radius: 15px;
    padding: 8px 12px;
    cursor: pointer;
    display: flex;
    align-items: center;

    &:hover {
        background-color: rgba(160, 218, 251, 0.5);
    }
`;

const TagIcon = styled.span`
    margin-right: 5px; /* 아이콘과 태그 텍스트 간의 간격 */
`;

const NoOptions = styled.div`
    padding: 10px;
    color: #aaa;
`;

export default TagSelector;
