import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import Nav from "../../components/Nav";
import Section1 from "../../components/Section1";
import Section2 from "../../components/Section2";
import {useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const MainPage = () => {
  const navigate = useNavigate();
  const [popularProjects, setPopularProjects] = useState([]);
  const [allProjects, setAllProjects] = useState([]);


  const handleAddButtonClick = () => {
      navigate('/WritePage'); 
  };

  // Fetching logic for popularProjects and allProjects
  const fetchPopularProjects = async () => {
    const response = await axios.get('/data.json');
    const projectsWithLikes = response.data.map(project => ({
      ...project,
      liked: false,
      likesCount: project.likesCount || 0
    }));
    setPopularProjects(projectsWithLikes);
  };

  const fetchAllProjects = async () => {
    const response = await axios.get('/data.json');
    const projectsWithLikes = response.data.map(project => ({
      ...project,
      liked: false,
      likesCount: project.likesCount || 0
    }));
    setAllProjects(projectsWithLikes);
  };

  useEffect(() => {
    fetchPopularProjects();
    fetchAllProjects();
  }, []);


  const handleLikeClickSection1 = (projectIndex) => {
    setPopularProjects((prevProjects) => {
      const newProjects = [...prevProjects];
      const project = newProjects[projectIndex];
      project.liked = !project.liked;
      project.likesCount += project.liked ? 1 : -1;
      return newProjects;
    });
  };

  const handleLikeClickSection2 = (projectIndex) => {
    setAllProjects((prevProjects) => {
      const newProjects = [...prevProjects];
      const project = newProjects[projectIndex];
      project.liked = !project.liked;
      project.likesCount += project.liked ? 1 : -1;
      return newProjects;
    });
  };

  useEffect(() => {
    // Fetching logic for popularProjects and allProjects
  }, []);



  return (
    <>
    <Nav /> 
    <MainWrapper>
    <Section1 
          projects={popularProjects} 
          onLikeClick={handleLikeClickSection1} 
        />
        <Section2 
          projects={allProjects} 
          onLikeClick={handleLikeClickSection2} 
        />

    <AddButton onClick={handleAddButtonClick}> 피드 작성하기 </AddButton>
    </MainWrapper>
    </>
  );
}

export default MainPage;

const MainWrapper = styled.div`
 margin-top: calc(100vh - 38%);
 min-height: calc(100vh - 250px);
 display: flex;
//  margin-top:30px;
padding-top: 30px;
 flex-direction: column;
 align-items: center;

`;


const AddButton = styled.div`
position: fixed;
right:5%;
top: calc(100% - 100px);
border: 1px solid #ddd;
border-radius: 30px 30px 1px 30px; //반시계 방향
padding: 10px 15px;
box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
background-color:#62B9EC;
color: white;
font-weight: bold;
  cursor: pointer;

  &:hover {
    background-color: #A0DAFB;
  }

`;
