// WritePage.js
import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const WritePage = () => {
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [participants, setParticipants] = useState(0);
  const [deadline, setDeadline] = useState('');
  const [progress, setProgress] = useState('');
  const [projectRole, setProjectRole] = useState('');
  const [description, setDescription] = useState('');

  const handleSave = () => {
    // 저장 로직 추가
    console.log({
      title,
      participants,
      deadline,
      progress,
      projectRole,
      description,
    });
    navigate('/'); // 메인 페이지로 이동
  };

  return (
    <>
    <Head/>
    <WriteWrapper>
      <BackButton onClick={() => navigate(-1)}>◀ 돌아가기</BackButton>
      <Title>제목을 적어주세요</Title>
      <Form>
        <Label>모집구분 | 프로젝트</Label>
        <Input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="프로젝트 제목" />

        <Label>모집인원</Label>
        <Input type="number" value={participants} onChange={(e) => setParticipants(e.target.value)} />

        <Label>마감일</Label>
        <Input type="date" value={deadline} onChange={(e) => setDeadline(e.target.value)} />

        <Label>진행장소</Label>
        <Input value={progress} onChange={(e) => setProgress(e.target.value)} placeholder="진행장소" />

        <Label>모집 역할</Label>
        <Input value={projectRole} onChange={(e) => setProjectRole(e.target.value)} placeholder="모집 역할" />

        <Label>기술 태그</Label>
        <Input placeholder="AWS, Blockchain 등" />

        <Label>본문을 작성해주세요.</Label>
        <TextArea value={description} onChange={(e) => setDescription(e.target.value)} placeholder="본문 작성..." />

        <SaveButton onClick={handleSave}>저장하기</SaveButton>
      </Form>
    </WriteWrapper>
    </>
  );
};

export default WritePage;

// Styled Components
const WriteWrapper = styled.div`
  padding: 20px;
`;

const BackButton = styled.button`
  margin-bottom: 20px;
`;

const Title = styled.h2`
  font-size: 24px;
  margin-bottom: 20px;
`;

const Form = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const Label = styled.label`
  font-weight: bold;
`;

const Input = styled.input`
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 5px;
`;

const TextArea = styled.textarea`
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 5px;
  min-height: 100px;
`;

const SaveButton = styled.button`
  margin-top: 20px;
  padding: 10px;
  border: none;
  border-radius: 5px;
  background-color: #62b9ec;
  color: white;
  font-weight: bold;
  cursor: pointer;

  &:hover {
    background-color: #a0dafb;
  }
`;
