import React from 'react';
import MuiPagination from '@mui/material/Pagination'; // Material-UI Pagination을 다른 이름으로 임포트
import styled from 'styled-components';

const Pagination = ({ currentPage, totalProjects, projectsPerPage, onPageChange }) => {
  const pageCount = Math.ceil(totalProjects / projectsPerPage);

  return (
    <MuiPagination
      count={pageCount}
      page={currentPage}
      onChange={(event, value) => onPageChange(value)} 
      color="primary"
      variant="outlined"
      shape="rounded"
    />
  );
};

export default Pagination;


const StyledPagination = styled(MuiPagination)`
  & .MuiPaginationItem-root {
    margin: 0 5px;
    background-color: #e0f7fa; // 배경색
    &:hover {
      background-color: #b2ebf2; // 호버 시 배경색
    }
  }

  & .Mui-selected {
    background-color: #00796b; // 선택된 페이지 버튼의 배경색
    color: white; // 선택된 페이지 버튼의 텍스트 색상
  }
`;
