import React, { useState } from 'react';
import styled from 'styled-components';
import Nav from '../../components/Nav';
import Dropdown from '../../components/DropDown';
import Modal from '../../components/Modal';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';




const WritePage = () => {
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [participants, setParticipants] = useState(0);
  const [deadline, setDeadline] = useState('');
  const [progress, setProgress] = useState('');
  const [description, setDescription] = useState('');
  const isLoggedIn = true; 
  const showSearch = false;

  const [selectedRoles, setSelectedRoles] = useState([]); // State to store selected roles with counts

  const handleRoleSelect = (role, count) => {
    const updatedRoles = selectedRoles.filter(r => r.role !== role);
    setSelectedRoles([...updatedRoles, { role, count }]);
  };

  const handleSave = () => {
    const dataToSend = {
      title,
      content: description,
      postStatus: true,
      savedFeed: false,
      tags: selectedTags,
      recruitmentNum: participants,
      deadline: new Date(deadline).toISOString(),
      place: progress,
      period: 3,
      roles: selectedRoles.reduce((acc, role) => {
        acc[role.role] = role.count;
        return acc;
      }, {}),
    };

    fetch('http://localhost:8080/feeds/create?feedType=PROJECT', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'YOUR_API_KEY',
      },
      body: JSON.stringify(dataToSend),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      console.log('Success:', data);
      navigate('/MainPage');
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  };

  const option1 = [
    { value: '기간 미정', label: '기간 미정' },
    { value: '1개월', label: '1개월' },
    { value: '2개월', label: '2개월' },
    { value: '3개월', label: '3개월' },
    { value: '4개월', label: '4개월' },
    { value: '5개월', label: '5개월' },
    { value: '6개월이상', label: '6개월이상' },
    { value: '장기', label: '장기' },
];

const option2 = [
  { value: '백엔드', label: '백엔드' },
  { value: '프론트엔드', label: '프론트엔드' },
  { value: '디자이너', label: '디자이너' },
  { value: 'PM', label: 'PM' },
  { value: 'AI 엔지니어', label: 'AI 엔지니어' },
  { value: '아키텍트', label: '아키텍트' },
  { value: '시스템 엔지니어', label: '시스템 엔지니어' },
  { value: '정보보안', label: '정보보안' },
  { value: 'DBA', label: 'DBA' },
  { value: '블록체인 엔지니어', label: '블록체인 엔지니어' },
  { value: '데이터 엔지니어', label: '데이터 엔지니어' },
  { value: '빅데이터', label: '빅데이터' },
  { value: 'DevOps', label: 'DevOps' },
  { value: '모바일', label: '모바일' },
  { value: 'QA', label: 'QA' },
  { value: 'SRE', label: 'SRE' },
  { value: 'iOS', label: 'iOS' },
  { value: '안드로이드', label: '안드로이드' },
  { value: '기획자', label: '기획자' },
  { value: '마케팅', label: '마케팅' },
];

const option3 = [
  { value: '웹', label: '웹' },
  { value: '모바일', label: '모바일' },
  { value: '정보보안', label: '정보보안' },
  { value: 'AWS', label: 'AWS' },
  { value: 'Git', label: 'Git' },
  { value: 'Github', label: 'Github' },
  { value: '클라우드', label: '클라우드' },
  { value: '블록체인', label: '블록체인' },
  { value: '인공지능', label: '인공지능' },
  { value: '빅데이터', label: '빅데이터' },
  { value: 'Spring Boot', label: 'Spring Boot' },
  { value: 'React', label: 'React' },
  { value: 'Vue', label: 'Vue' },
  { value: 'Python', label: 'Python' },
  { value: 'Node.js', label: 'Node.js' },
  { value: 'TypeScript', label: 'TypeScript' },
  { value: '게임', label: '게임' },
  { value: 'UI/UX', label: 'UI/UX' },
  { value: '알고리즘', label: '알고리즘' },
  { value: '자료구조', label: '자료구조' },
  { value: 'C/C++', label: 'C/C++' },
  { value: 'C#', label: 'C#' },
  { value: 'SQL', label: 'SQL' },
  { value: 'NoSQL', label: 'NoSQL' },
  { value: 'Django', label: 'Django' },
  { value: 'Figma', label: 'Figma' },
  { value: 'Swift', label: 'Swift' },
  { value: 'Kotlin', label: 'Kotlin' },
  { value: 'React Native', label: 'React Native' },
  { value: 'Android', label: 'Android' },
  { value: 'iOS', label: 'iOS' },
  { value: 'GCP', label: 'GCP' },
  { value: 'Kubernetes', label: 'Kubernetes' },
  { value: 'Docker', label: 'Docker' },
  { value: 'Ruby', label: 'Ruby' },
  { value: 'R', label: 'R' },
  { value: 'Go', label: 'Go' },
  { value: 'Next.js', label: 'Next.js' },
  { value: 'Express', label: 'Express' },
  { value: 'Firebase', label: 'Firebase' },
  { value: 'Linux/Unix', label: 'Linux/Unix' },
  { value: '데이터마이닝', label: '데이터마이닝' },
  { value: 'Solidity', label: 'Solidity' },
];



const [isModalOpen, setIsModalOpen] = useState(false); // 모달 열기 상태
const [selectedTags, setSelectedTags] = useState([]); // 선택된 태그 상태

const handleAddTagClick = () => {
    setIsModalOpen(true); 
};

const closeModal = () => {
    setIsModalOpen(false); 
};

const handleTagSelect = (option) => {
  if (!selectedTags.includes(option.label)) {
      setSelectedTags([...selectedTags, option.label]); // 선택된 태그 추가
  }
};



  return (
    <>
      <Nav isLoggedIn={isLoggedIn} showSearch={showSearch} />
      <WriteWrapper>
      <BackButton onClick={() => navigate(-1)} style={{ display: 'flex', alignItems: 'center' }}>
      <FontAwesomeIcon icon={faArrowLeft} style={{ marginRight: '5px' }} />
      이전
    </BackButton>


        <Form>


          <TitleInput>
          <InputField
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="제목을 적어주세요"
          />

          </TitleInput>
          </Form>


        <Container>
        <Form> 
        <Title>
          <Label>모집 구분 | 프로젝트</Label>
          </Title> 

        <InputBox>



          <InputWrapper>

          <Label>마감일자</Label>
          <Input
            type="date"
            value={deadline}
            onChange={(e) => setDeadline(e.target.value)}
          />
          </InputWrapper>

          <InputWrapper>

          <Label>진행장소</Label>
          <Input
            value={progress}
            onChange={(e) => setProgress(e.target.value)}
            placeholder="진행장소"
          />
          </InputWrapper>


          <InputWrapper>   
          <Label>모집 역할</Label>

          <Dropdown 
            options={option2} 
            placeholder={"프론트엔드,백엔드..."} 
            showCountButtons={true}
            onRoleSelect={handleRoleSelect} // Pass the handler to Dropdown
          />
          </InputWrapper>


          <InputWrapper>   
          <Label> 진행기간 </Label>
          <Dropdown options={option1} placeholder={"기간미정~6개월이상"}/>

          </InputWrapper>
          </InputBox>

        </Form>

        <TagsSection>
           {/* 선택된 태그 버튼 표시 */}
           {selectedTags.map((tag, index) => (
                <TagButton key={`${tag}-${index}`}>{tag}</TagButton>
            ))}
            <TagAdd onClick={handleAddTagClick}> + 태그 추가하기</TagAdd>

            <Modal isOpen={isModalOpen} onClose={closeModal}>
                <Dropdown 
                    options={option3} 
                    placeholder={"태그를 선택하시오"}
                    onTagSelect={handleTagSelect} // 태그 선택을 처리하기 위해 이 prop을 전달
                />
            </Modal>
          </TagsSection>

        </Container>

        <Body>
        <Form>

          <TextArea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="본문을 작성해주세요"
          />

        </Form>
        </Body>

        <Submit>
        <SaveButton onClick={handleSave}>임시저장</SaveButton>
        <SaveButton onClick={handleSave}>저장하기</SaveButton>
        </Submit>
      </WriteWrapper>
    </>
  );
};

