import React, { useState } from 'react';
import styled from 'styled-components';
import ApplyHead from "../../components/ApplyHead";
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faHeart, faComment } from '@fortawesome/free-solid-svg-icons';


const ApplyPage = () => {
  const navigate = useNavigate(); 

  const [comments, setComments] = useState([]);
  const [commentInput, setCommentInput] = useState('');
  const [liked, setLiked] = useState(false);

  const handleCommentSubmit = () => {
    if (commentInput.trim()) {
      setComments([...comments, commentInput]);
      setCommentInput('');
    }
  };

  const handleLike = () => {
    setLiked(!liked);
  };

  return (
    <>
      <ApplyHead/>
      <Container>
        <BackButton onClick={() => navigate(-1)} style={{ display: 'flex', alignItems: 'center' }}>
          <FontAwesomeIcon icon={faArrowLeft} style={{ marginRight: '5px' }} />
          ÎèåÏïÑÍ∞ÄÍ∏∞
        </BackButton>

        <Title> Ïû¨ÎÇú ÎåÄÏùë Ïñ¥ÌîåÎ¶¨ÏºÄÏù¥ÏÖò Í∞úÎ∞ú Î∞±ÏóîÎìú Íµ¨Ìï¥Ïöî! </Title>
        <LikeButton onClick={handleLike}>
            <FontAwesomeIcon icon={faHeart} style={{ color: liked ? 'red' : 'grey' }} />
            {/* {liked ? ' Ï¢ãÏïÑÏöî Ï∑®ÏÜå' : ' Ï¢ãÏïÑÏöî'} */}
          </LikeButton>

        <Post>
          <PostDetails>
            <Detail>Î™®ÏßëÍµ¨Î∂Ñ: ÌîÑÎ°úÏ†ùÌä∏</Detail>
            <Detail>Î™®ÏßëÏù∏Ïõê: 2/4</Detail>
            <Detail>ÎßàÍ∞êÏùº: 2024.07.29</Detail>
            <Detail>ÏßÑÌñâÍ∏∞Í∞Ñ: 3Í∞úÏõî</Detail>
            <Detail>ÏßÑÌñâÏû•ÏÜå: Í∞ïÎÇ®Ïó≠</Detail>
            <Detail>Ïã†Ï≤≠ÏûêÏàò: Î∞±ÏóîÎìú(3), ÎîîÏûêÏù¥ÎÑà(1)</Detail>
            <LastDetail>Î™®Ïßë Ïó≠Ìï†: Î∞±ÏóîÎìú(2), ÎîîÏûêÏù¥ÎÑà(1)</LastDetail>
          </PostDetails>

          <TagsSection>
          <TagButton>AWS</TagButton>
          <TagButton>Blockchain</TagButton>
          </TagsSection>


          <ApplyButton>Ïã†Ï≤≠ÌïòÍ∏∞</ApplyButton>


        </Post>

        <PostDescription>
          Î∏îÎ°ùÏ≤¥Ïù∏ DAppÏùÑ Í∞úÎ∞úÌï† ÏòàÏ†ïÏûÖÎãàÎã§! ÏïÑÏßÄ Ïñ¥Îñ§ Ïñ¥ÌîåÎ¶¨ÏºÄÏù¥ÏÖòÏùò ÏïÑÏù¥ÎîîÏñ¥Îäî ÎØ∏Ï†ïÏù¥Î©∞, ÌîÑÎ°úÏ†ùÌä∏ ÌåÄÏõêÏù¥ Î™®Ïù¥Î©¥ Í∞ôÏù¥ Î∏åÎ†àÏù∏Ïä§ÌÜ†Î∞çÏùÑ ÌÜµÌï¥ Í≤∞Ï†ïÌï† ÏòàÏ†ïÏûÖÎãàÎã§.
          Î∏îÎ°ùÏ≤¥Ïù∏ DAppÏùÑ Í∞úÎ∞úÌï† ÏòàÏ†ïÏûÖÎãàÎã§! ÏïÑÏßÄ Ïñ¥Îñ§ Ïñ¥ÌîåÎ¶¨ÏºÄÏù¥ÏÖòÏùò ÏïÑÏù¥ÎîîÏñ¥Îäî ÎØ∏Ï†ïÏù¥Î©∞, ÌîÑÎ°úÏ†ùÌä∏ ÌåÄÏõêÏù¥ Î™®Ïù¥Î©¥ Í∞ôÏù¥ Î∏åÎ†àÏù∏Ïä§ÌÜ†
          Î∏îÎ°ùÏ≤¥Ïù∏ DAppÏùÑ Í∞úÎ∞úÌï† ÏòàÏ†ïÏûÖÎãàÎã§! ÏïÑÏßÄ Ïñ¥Îñ§ Ïñ¥ÌîåÎ¶¨ÏºÄÏù¥ÏÖòÏùò ÏïÑÏù¥ÎîîÏñ¥Îäî ÎØ∏Ï†ïÏù¥Î©∞, ÌîÑÎ°úÏ†ùÌä∏ ÌåÄÏõêÏù¥ Î™®Ïù¥Î©¥ Í∞ôÏù¥ Î∏åÎ†àÏù∏Ïä§ÌÜ†
        </PostDescription>


        <AuthorSection>
          <ChatButton>
            <FontAwesomeIcon icon={faComment} />
            Ï±ÑÌåÖ
          </ChatButton>

          <AuthorID> üë§ ÏûëÏÑ±Ïûê: ÏÇ¨Ïö©Ïûê123</AuthorID>
          </AuthorSection>


        <CommentsSection>
          <CommentsTitle>ÎåìÍ∏Ä ({comments.length})</CommentsTitle>
          <CommentsList>
            {comments.map((comment, index) => (
              <Comment key={index}>{comment}</Comment>
            ))}
          </CommentsList>
          <CommentInputWrapper>
            <CommentInput
              value={commentInput}
              onChange={(e) => setCommentInput(e.target.value)}
              placeholder="ÎåìÍ∏ÄÏùÑ ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî."
            />
            <CommentButton onClick={handleCommentSubmit}>Îì±Î°ù</CommentButton>
          </CommentInputWrapper>
        </CommentsSection>
      </Container>
    </>
  );
};

export default ApplyPage;

const Container = styled.div`
  position: relative;
  padding: 20px;
  margin-top: calc(100vh - 38%);
  min-height: calc(100vh - 250px);
  display: flex;
  flex-direction: column;
  align-items: center;
