import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import {useLocation, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faSlidersH } from '@fortawesome/free-solid-svg-icons';
import { faBell, faCommentDots, faUser } from '@fortawesome/free-solid-svg-icons';
import { faBell as regularBell, faCommentDots as regularComment, faUser as regularUser } from '@fortawesome/free-regular-svg-icons'; // 라인 아이콘 임포트



const Nav = () => {

  // const navigate = useNavigate();

  //   const handleAddButtonClick = () => {
  //       navigate('/writePage'); // writePage로 이동
  //   };

  const [visible, setVisible] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);

  // const [toggleActive, setToggleActive] = useState(false); // 토글 상태 추가
  //로컬 스토리지에서 초기값 가져오기
  const [toggleActive, setToggleActive] = useState(() => {
    // 로컬 스토리지에서 초기값 가져오기
    const storedToggleState = localStorage.getItem('toggleActive');
    return storedToggleState !== null ? JSON.parse(storedToggleState) : false;
  });


  const [filterVisible, setFilterVisible] = useState(false); // 필터 가시성 상태 추가
  const [selectedTags, setSelectedTags] = useState([]); // 선택된 태그 상태 추가

  const handleScroll = () => {
    if (typeof window !== 'undefined') {
      const currentScrollY = window.scrollY;
      setVisible(lastScrollY > currentScrollY || currentScrollY < 10);
      setLastScrollY(currentScrollY);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [lastScrollY]);


  useEffect(() => {
    // 로컬 스토리지에서 토글 상태 가져오기
    const storedToggleState = localStorage.getItem('toggleActive');
    if (storedToggleState !== null) {
      setToggleActive(JSON.parse(storedToggleState));
    }
  }, []);

  // useEffect(() => {
  //   // 토글 상태가 변경될 때마다 로컬 스토리지에 저장
  //   localStorage.setItem('toggleActive', JSON.stringify(toggleActive));
  // }, [toggleActive]);

  const handleToggleChange = () => {
    setToggleActive(!toggleActive); // 토글 상태 변경
  };

  // const handleToggleChange = () => {
  //   setToggleActive((prev) => !prev);
  // };   // 이전 상태 기반


  const toggleFilterVisibility = () => {
    setFilterVisible(!filterVisible); // 필터 가시성 토글
  };


  const handleTagClick = (tag) => {
    setSelectedTags((prev) =>
      prev.includes(tag) ? prev.filter((t) => t !== tag) : [...prev, tag]
    );
  };



  return (
    <NavWrapper visible={visible}>
      <Logo onClick={() => (window.location.href = "/")}>
        <span>iTeaMoa</span>
      </Logo>
      <ToggleContainer>
        <ToggleLabel active={toggleActive}>STUDY</ToggleLabel>
        <ToggleSwitch type="checkbox" checked={toggleActive} onChange={handleToggleChange} />
        <ToggleLabel active={!toggleActive}>PROJECT</ToggleLabel>
      </ToggleContainer>
      <SearchContainer>
        <SearchInput placeholder="Search projects" />
        <SearchIcon><FontAwesomeIcon icon={faSearch} /></SearchIcon>

        <FilterIcon onClick={toggleFilterVisibility}>
        <FontAwesomeIcon icon={faSlidersH} />
      </FilterIcon>

      </SearchContainer>


    {filterVisible && (
      <TagContainer>
        <Tag>AWS</Tag>
        <Tag>Blockchain</Tag>
        <Tag>NodeJS</Tag>
        <Tag>React</Tag>
        <Tag>Java</Tag>
      </TagContainer>
       )}



<UserActions>
<UserIcon  className="bell" > <FontAwesomeIcon icon={regularBell} size="15px" /></UserIcon>
        <UserIcon className="comment">     <FontAwesomeIcon icon={regularComment} size="15px" /></UserIcon>
        <UserIcon  className="user" >  <FontAwesomeIcon icon={regularUser} size="15px" /></UserIcon>
        <LogoutButton>   로그아웃</LogoutButton>
    </UserActions>
    </NavWrapper>
  );
}

export default Nav;

// Styled Components
const NavWrapper = styled.nav`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 35%;
  background-color: #ffffff;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 36px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  z-index: 3;
  transition: transform 0.5s ease;
  transform: translateY(${props => (props.visible ? '0' : '-100%')});
`;

const Logo = styled.div`
  position: absolute; 
  top: 20%; 
  cursor: pointer;
  font-size: 40px;
  color: #0080ff; 
`;

const ToggleContainer = styled.div`
  display: flex;
  align-items: center;
  position: absolute;
  top: 35%;
  cursor: pointer;
  font-size: 24px;
`;

const ToggleLabel = styled.label`
  margin: 0 10px;
  font-size: 16px;
  font-weight: bold;
  color: ${props => (props.active ? '#aaa' : '#0080ff')}; // 활성화 여부에 따라 색상 변경
`;

const ToggleSwitch = styled.input`
  appearance: none;
  width: 50px;
  height: 30px;
  background: #ccc; 
  border-radius: 30px; 
  position: relative;
  cursor: pointer;
  outline: none;

  &:checked {
    background: #62B9EC; 
  }

  &:checked::before {
    transform: translateX(24px); 
  }

  &::before {
    content: '';
    position: absolute;
    width: 26px; 
    height: 26px; 
    top: 7%;
    border-radius: 50%; 
    background: white; 
    transition: transform 0.2s; 
  }
`;

const SearchContainer = styled.div`
  position: absolute;
  display: flex;
  align-items: center;
  margin-right: 20px; /* 간격 조정 */
  margin-top: 60px;
  width: 400px; /* 검색창의 전체 너비를 설정 */
`;

const SearchInput = styled.input`
  width: 100%; /* 너비를 100%로 설정하여 길이 늘리기 */
  padding: 15px 40px;
  border: 2px solid #0080ff;
  border-radius: 15px;
  outline: none;
  font-size: 16px;
  background-color: #F3F0F0;

  &::placeholder {
    color: #aaa;
  }
`;

const SearchIcon = styled.span`
  position: absolute;
  right: 20%;
  top: 50%;
  transform: translate(250%, -50%);
  font-size: 20px;
  cursor: pointer;
  color: #0080ff;

  &:hover {
    color: #62B9EC;
  }
`;


const FilterIcon = styled.div`
  position: absolute;
  top: 20%;
  left: 100%;
  border: 1px none #0080ff;
  background-color: #F3F0F0;  
  margin: 0 10px;
  padding: 5px 10px;
  border-radius: 15px;
  font-size: 22px;
  color: #aaa;
  cursor: pointer;

  &:hover {
    color: grey;


`;


const TagContainer = styled.div`
  position: absolute;
  top: 70%;
  margin-top:10px;
  border: 2px solid #62B9EC;
  border-radius: 30px 5px 30px 30px;
  display: flex;
  flex-wrap: wrap; 
`;

const Tag = styled.div`
  margin: 20px;
  padding: 10px 15px;
  border: 1px none #0080ff;
  background-color: #F3F0F0; 
  border-radius: 15px;
  color: #0080ff;
  font-size: 14px;
  cursor: pointer;

  @media (max-width: 768px) {
         font-size: 12px; // 작은 화면에서 폰트 크기 조정
         padding: 4px 8px; }
`;


const UserActions = styled.div`
  position: absolute;
  top: 10%;
  right: 5%;
  display: flex;
  align-items: center;
  color: #0080ff;
`;


const LogoutButton = styled.div`
  border: 1px none #0080ff;
  background-color: #3563E9;  
  margin: 0 10px;
  padding: 8px 16px;
  border-radius: 5px;
  font-size: 16px;
  color: white;
  cursor: pointer;

  &:hover {
    color: #0080ff;
  }
`;

const UserIcon = styled.div`
  margin: 0 10px;
  font-size: 25px;
  cursor: pointer;
  color: grey;

  &:hover {
