import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import ApplyHead from "../../components/ApplyHead";
import { useNavigate , useParams } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faHeart, faComment } from '@fortawesome/free-solid-svg-icons';
import {  faUser as regularUser } from '@fortawesome/free-regular-svg-icons'; 
import items from '../../data.js'; // 임의 데이터 파일 import



const ApplyPage = () => {
  const navigate = useNavigate(); 
  const {projectId } = useParams(); // URL에서 projectId 가져오기
  const [comments, setComments] = useState([]);
  const [commentInput, setCommentInput] = useState('');
  const [project, setProject] = useState(null);
  const [liked, setLiked] = useState(false);



  const fetchProjectDetails = () => {
    const selectedProject = items.find(item => item.pk === projectId);
    setProject(selectedProject);
  }

  useEffect(() => {
    fetchProjectDetails();
  }, [projectId]);

  const handleLike = () => {
    setLiked(!liked);
  };

  const handleCommentSubmit = () => {
    if (commentInput.trim()) {
      setComments([...comments, commentInput]);
      setCommentInput('');
    }
  };

  return (
    <>
      <ApplyHead/>
      <Container>
        <BackButton onClick={() => navigate(-1)} style={{ display: 'flex', alignItems: 'center' }}>
          <FontAwesomeIcon icon={faArrowLeft} style={{ marginRight: '5px' }} />
          이전
        </BackButton>

        <Title> {project.title} </Title>
        <LikeButton onClick={handleLike}>
            <FontAwesomeIcon icon={faHeart} style={{ color: liked ? 'red' : 'grey' }} />
            {/* {liked ? ' 좋아요 취소' : ' 좋아요'} */}
          </LikeButton>

        <Post>
          <PostDetails>
            <Detail>모집구분 | 프로젝트</Detail>
            <Detail>마감일 | {new Date(project.deadline).toLocaleDateString()}</Detail>
            <Detail>모집현황 | {project.recruitmentnum}명 </Detail>
            <Detail>게시일자 |  {new Date(project.timestamp).toLocaleDateString()}</Detail>
            <Detail>진행기간 | {project.period}개월</Detail>
            <Detail>진행장소 | {project.place}</Detail>
            <Detail>신청자수 | 백엔드(3), 디자이너(1)</Detail>
            <Detail>모집 역할 | 백엔드(2), 디자이너(1)</Detail>
          </PostDetails>

          <TagsSection>
          {project.tags.map((tag, index) => (
              <TagButton key={index}>{tag}</TagButton>
            ))}
          </TagsSection>


          <ApplyButton>신청하기</ApplyButton>


        </Post>

        <PostDescription>

        {project.content}
          블록체인 DApp을 개발할 예정입니다! 아지 어떤 어플리케이션의 아이디어는 미정이며, 프로젝트 팀원이 모이면 같이 브레인스토밍을 통해 결정할 예정입니다.
          블록체인 DApp을 개발할 예정입니다! 아지 어떤 어플리케이션의 아이디어는 미정이며, 프로젝트 팀원이 모이면 같이 브레인스토
          블록체인 DApp을 개발할 예정입니다! 아지 어떤 어플리케이션의 아이디어는 미정이며, 프로젝트 팀원이 모이면 같이 브레인스토
        </PostDescription>


        <AuthorSection>
          <ChatButton>
            <FontAwesomeIcon icon={faComment} />
            채팅
          </ChatButton>

          <AuthorID> <FontAwesomeIcon icon={regularUser} style={{ fontSize: '20px', lineHeight: '1.2', marginRight: '6px' }} /> 작성자: 사용자123</AuthorID>
          </AuthorSection>


        <CommentsSection>
          <CommentsTitle>댓글 ({comments.length})</CommentsTitle>
          <CommentsList>
            {comments.map((comment, index) => (
              <Comment key={index}>{comment}</Comment>
            ))}
          </CommentsList>
          <CommentInputWrapper>
            <CommentInput
              value={commentInput}
              onChange={(e) => setCommentInput(e.target.value)}
              placeholder="댓글을 입력해 주세요."
            />
            <CommentButton onClick={handleCommentSubmit}>등록</CommentButton>
          </CommentInputWrapper>
        </CommentsSection>
      </Container>
    </>
  );
};

export default ApplyPage;

const Container = styled.div`
