import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import axios from 'axios';
import {  faUser as regularUser, faHeart as regularHeart } from '@fortawesome/free-regular-svg-icons'; 
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom'; // useNavigate 훅 import

//각 섹션의 데이터를 상태로 관리합니다: useState를 사용하여 데이터를 저장하고, 
//useEffect를 통해 컴포넌트가 마운트될 때 데이터를 Fetch합니다.

function Section1({ onProjectClick }) {
  const [popularProjects, setPopularProjects] = useState([]);
  //    [ 현재 상태값, 상태 업데이트 함수]
  const navigate = useNavigate(); // useNavigate 훅을 사용하여 navigate 함수 생성
  const [project, setProject] = useState(null);



  // //단순화한 코드
  // const fetchPopularProjects = async () => {
  //   const response = await axios.get('/data.json');
  //   setPopularProjects(response.data);
  // };


  const fetchPopularProjects = async () => {
    try {
      const response = await axios.get('/data.json');
      // 각 프로젝트에 liked 속성과 초기 likesCount 추가
      const projectsWithLikes = response.data.map(project => ({
        ...project,
        liked: false, // 초기 상태는 좋아요가 눌리지 않은 상태
        likesCount: project.likesCount || 0 // likesCount가 없으면 0으로 초기화
      }));
      setPopularProjects(projectsWithLikes);
    } catch (error) {
      console.error('Error fetching popular projects:', error);
    }
  };

  // const fetchPopularProjects = async () => {
  //   try {
  //     const response = await axios.get('/api/projects/popular'); // 인기 프로젝트 데이터 요청
  //     setPopularProjects(response.data);
  //   } catch (error) {
  //     console.error('Error fetching popular projects:', error);
  //   }
  // };


  useEffect(() => {
    fetchPopularProjects();
  }, []);


    // 클릭된 프로젝트의 ID를 사용하여 상세 페이지로 이동
  const handleProjectClick = (project) => {
    const modifiedPk = project.pk.replace('#', ''); // pk에서 # 제거/ React Router의 useParams는 해시를 처리하지 않음
    navigate(`/ApplyPage/${modifiedPk}`); // 수정된 pk로 상세 페이지로 이동
  };


  //  const handleLikeClick = (projectIndex) => {
  //   setPopularProjects((prevProjects) => { //이전 배열 복사
  //     const newProjects = [...prevProjects];
  //     newProjects[projectIndex].likesCount++; 


  //     return newProjects;
  //   });
  // };


  const handleLikeClick = (projectIndex) => {
    setPopularProjects((prevProjects) => {
      const newProjects = [...prevProjects];
      const project = newProjects[projectIndex];

      // 좋아요 상태를 토글
      const isLiked = project.liked; // 현재 liked 상태 저장
      project.liked = !isLiked; // 상태 토글

      // likesCount 업데이트
      project.likesCount += project.liked ? 1 : -1; // 좋아요 상태에 따라 증가 또는 감소

      return newProjects; // 업데이트된 배열 반환
    });
  };


  // const handleLikeClick = (projectIndex) => {
  //   setPopularProjects((prevProjects) => {
  //     const newProjects = [...prevProjects]; // 이전 프로젝트 배열을 복사
  //     const project = newProjects[projectIndex]; // 클릭된 프로젝트 가져오기

  //     // 좋아요 상태 토글
  //     project.liked = !project.liked;

  //     // likesCount 업데이트
  //     project.likesCount += project.liked ? 1 : -1; // 좋아요 추가 또는 취소

  //     return newProjects; // 업데이트된 프로젝트 배열 반환
  //   });
  // };



  return (
    <SectionWrapper>
      <SectionTitle>인기 프로젝트</SectionTitle>
      <ProjectList>
        {popularProjects.slice(0, 3).map((project, index) => (
          <ProjectCard key={index} onClick={() => handleProjectClick(project)}>
            <ProjectOwner> <FontAwesomeIcon icon={regularUser} style={{ fontSize: '20px', lineHeight: '1.2', marginRight: '6px' }} />{project.creatorID}</ProjectOwner>
            <ProjectLikes  onClick={(e) => { e.stopPropagation(); handleLikeClick(index); }} >
            <FontAwesomeIcon icon={project.liked ? faHeart : regularHeart} style={{ color: project.liked ? 'red' : 'white', marginRight: '4px' }} />
              {project.likesCount}
            </ProjectLikes>
            <ProjectTitle>{project.title}</ProjectTitle>
            <ProjectInfo>
              <Tags>
                {project.tags.map((tag, tagIndex) => (
                  <Tag key={tagIndex}>{tag}</Tag>
                ))}
              </Tags>
              <Details>
                모집인원 | {project.recruitmentNum}명 
                </Details>
                <Details>
                 마감일 | {new Date(project.deadline).toLocaleDateString()}
              </Details>
            </ProjectInfo>
          </ProjectCard>
        ))}
      </ProjectList>
    </SectionWrapper>
  );
}


const SectionWrapper = styled.div`
  width: calc(100% / 2 + 100px);
  border: 2px solid #A0DAFB;
  border-radius: 30px 30px 1px 30px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: rgba(160, 218, 251, 0.5);
`;

const SectionTitle = styled.h2`
  font-size: 24px;
  margin-bottom: 20px;
`;

const ProjectList = styled.div`
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  gap: 20px;
`;

const ProjectCard = styled.div`
  position: relative;
  border: 2px solid #A0DAFB;
  border-radius: 30px 30px 1px 30px;
  padding: 15px;
  margin-bottom: 20px;
  width: calc(100% / 4);
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: white;
  cursor: pointer;

  &:hover {
    background-color: #A0DAFB;
  }

  @media (max-width: 1200px) {
    width: calc(33.33% - 20px); /* 3열로 조정 */
  }

  @media (max-width: 768px) {
    width: calc(50% - 20px); /* 2열로 조정 */
  }

  @media (max-width: 480px) {
    width: 100%; /* 1열로 조정 */
  }
`;

const ProjectOwner = styled.div`
  font-weight: bold;
  position: absolute;
  color: #858585;
`;

const ProjectLikes = styled.div`
  border: 1px solid #ddd;
  padding: 2px 8px;
  border-radius: 15px;
  color: white;
  font-weight: bold;
  right: 10%;
  position: absolute;
  background-color: #C4C4C4;
  cursor: pointer; 

  &:hover {
    background-color: #A0DAFB; 
  }
`;


const ProjectTitle = styled.div`
  font-size: 20px;
  margin: 20px 0;
  margin-top:40px;
  font-weight: bold;
  min-height: 40px;
  text-align: left;
`;

const ProjectInfo = styled.div`
  font-size: 14px;
  color: #777;
`;

const Tags = styled.div`
  display: flex;
  margin-bottom: 5px;
  align-items: space-between;
`;

const Tag = styled.div`
  margin-bottom: 5px;
  margin-right: 5px;
  border: 1px solid #ddd;
  border-radius: 14px 14px 1px 14px; //반시계 ㅔ방향
  padding: 5px 10px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: white;
  border-color: rgba(160, 218, 251);
  color: #0A8ED9;


  @media (max-width: 1800px) {
    font-size: 12px; /* 768px 이하에서 폰트 크기 줄이기 */
    padding: 4px 8px; /* 패딩 조정 */
  }

  @media (max-width: 480px) {
    font-size: 8px; /* 480px 이하에서 폰트 크기 줄이기 */
    padding: 3px 6px; /* 패딩 조정 */
  }



`;

const Details = styled.div`
    display: flex;
    justify-content: space-between;

`;


export default Section1;
