import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import {useLocation, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faSlidersH } from '@fortawesome/free-solid-svg-icons';
import { faBell as regularBell, faCommentDots as regularComment, faUser as regularUser } from '@fortawesome/free-regular-svg-icons'; // 라인 아이콘 임포트
import Modal from './Modal';
import TagSelector from './TagSelector';


const Nav = () => {

  const option3 = [
    { value: '웹', label: '웹' },
    { value: '모바일', label: '모바일' },
    { value: '정보보안', label: '정보보안' },
    { value: 'AWS', label: 'AWS' },
    { value: 'Git', label: 'Git' },
    { value: 'Github', label: 'Github' },
    { value: '클라우드', label: '클라우드' },
    { value: '블록체인', label: '블록체인' },
    { value: '인공지능', label: '인공지능' },
    { value: '빅데이터', label: '빅데이터' },
    { value: 'Spring Boot', label: 'Spring Boot' },
    { value: 'React', label: 'React' },
    { value: 'Vue', label: 'Vue' },
    { value: 'Python', label: 'Python' },
    { value: 'Node.js', label: 'Node.js' },
    { value: 'TypeScript', label: 'TypeScript' },
    { value: '게임', label: '게임' },
    { value: 'UI/UX', label: 'UI/UX' },
    { value: '알고리즘', label: '알고리즘' },
    { value: '자료구조', label: '자료구조' },
    { value: 'C/C++', label: 'C/C++' },
    { value: 'C#', label: 'C#' },
    { value: 'SQL', label: 'SQL' },
    { value: 'NoSQL', label: 'NoSQL' },
    { value: 'Django', label: 'Django' },
    { value: 'Figma', label: 'Figma' },
    { value: 'Swift', label: 'Swift' },
    { value: 'Kotlin', label: 'Kotlin' },
    { value: 'React Native', label: 'React Native' },
    { value: 'Android', label: 'Android' },
    { value: 'iOS', label: 'iOS' },
    { value: 'GCP', label: 'GCP' },
    { value: 'Kubernetes', label: 'Kubernetes' },
    { value: 'Docker', label: 'Docker' },
    { value: 'Ruby', label: 'Ruby' },
    { value: 'R', label: 'R' },
    { value: 'Go', label: 'Go' },
    { value: 'Next.js', label: 'Next.js' },
    { value: 'Express', label: 'Express' },
    { value: 'Firebase', label: 'Firebase' },
    { value: 'Linux/Unix', label: 'Linux/Unix' },
    { value: '데이터마이닝', label: '데이터마이닝' },
    { value: 'Solidity', label: 'Solidity' },
  ];



  const [visible, setVisible] = useState(true);
  const navigate = useNavigate();
  const [searchValue, setSearchValue] = useState("");

  // const [toggleActive, setToggleActive] = useState(false); // 토글 상태 추가
  //로컬 스토리지에서 초기값 가져오기
  const [toggleActive, setToggleActive] = useState(() => {
    // 로컬 스토리지에서 초기값 가져오기
    const storedToggleState = localStorage.getItem('toggleActive');
    return storedToggleState !== null ? JSON.parse(storedToggleState) : false;
  });


  const [selectedTags, setSelectedTags] = useState([]); // 선택된 태그 상태 추가
  const [isModalOpen, setIsModalOpen] = useState(false); // 모달 열기 상태


  useEffect(() => {
    // 로컬 스토리지에서 토글 상태 가져오기
    const storedToggleState = localStorage.getItem('toggleActive');
    if (storedToggleState !== null) {
      setToggleActive(JSON.parse(storedToggleState));
    }
  }, []);


  const handleToggleChange = () => {
    setToggleActive(!toggleActive); 
  };

  const toggleFilterVisibility = () => {
    setIsModalOpen(true)
  };


  // const handleTagClick = (tag) => {
  //   setSelectedTags((prev) =>
  //     prev.includes(tag) ? prev.filter((t) => t !== tag) : [...prev, tag]
  //   );
  // };



  const handleChange = (e) => {
    setSearchValue(e.target.value);
    console.log('e.target.value', e.target.value)

  }


  const handleAddButtonClick = () => {
    navigate(`/SearchPage?q=${searchValue}`);
  };


  const handleTagSelect = (option) => {
    setSelectedTags((prevTags) => {
        if (!prevTags.includes(option.label)) {
            return [...prevTags, option.label];
        }
        return prevTags;
    });
};

  const handleTagRemove = (tag) => {
      setSelectedTags((prevTags) => prevTags.filter((t) => t !== tag));
  };

  const handleResetTags = () => {
      setSelectedTags([]);
  };


  const closeModal = () => {
    setIsModalOpen(false); // 모달 닫기
  };



  return (
    <NavWrapper >
      <Logo>
      <img
          alt="Disney Plus Logo"
          src="/images/logo1.png"
        onClick={() => (window.location.href = "/")}/>
      </Logo>

      <ToggleContainer>
        <ToggleLabel active={toggleActive}>STUDY</ToggleLabel>
        <ToggleSwitch type="checkbox" checked={toggleActive} onChange={handleToggleChange} />
        <ToggleLabel active={!toggleActive}>PROJECT</ToggleLabel>
      </ToggleContainer>
      <SearchContainer>
      <SearchInput value={searchValue}
        onChange={handleChange} placeholder="Search projects" />
        <SearchIcon  onClick={handleAddButtonClick} ><FontAwesomeIcon icon={faSearch} /></SearchIcon>

        <FilterIcon onClick={toggleFilterVisibility}>
        <FontAwesomeIcon icon={faSlidersH} />
      </FilterIcon>

      </SearchContainer>

<UserActions>
<UserIcon  className="bell" > <FontAwesomeIcon icon={regularBell} size="15px" /></UserIcon>
        <UserIcon className="comment">     <FontAwesomeIcon icon={regularComment} size="15px" /></UserIcon>
        <UserIcon  className="user" >  <FontAwesomeIcon icon={regularUser} size="15px" /></UserIcon>
        <LogoutButton>   로그아웃</LogoutButton>
    </UserActions>


            {/* 태그 컨테이너: 선택된 태그가 있을 때만 표시 */}
            {selectedTags.length > 0 && (
                <TagContainer>
                    {selectedTags.map((tag) => (
                        <Tag key={tag}>
                            {tag}
                            <CloseButton onClick={() => handleTagRemove(tag)}>X</CloseButton>
                        </Tag>
                    ))}
                    <ResetButton onClick={handleResetTags}>초기화</ResetButton>
                </TagContainer>
            )}


            <Modal isOpen={isModalOpen} onClose={closeModal} modalType="nav" >
                <TagSelector
                    options={option3}
                    placeholder={"태그를 선택하시오"}
                    onTagSelect={handleTagSelect} 
                    selectedTags={selectedTags} 
                />
            </Modal>
    </NavWrapper>
  );
}

