import React, { useState } from 'react';
import styled from 'styled-components';
import ApplyHead from "../../components/ApplyHead";
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faHeart, faComment } from '@fortawesome/free-solid-svg-icons';


const ApplyPage = () => {
  const navigate = useNavigate(); 

  const [comments, setComments] = useState([]);
  const [commentInput, setCommentInput] = useState('');
  const [liked, setLiked] = useState(false);

  const handleCommentSubmit = () => {
    if (commentInput.trim()) {
      setComments([...comments, commentInput]);
      setCommentInput('');
    }
  };

  const handleLike = () => {
    setLiked(!liked);
  };

  return (
    <>
      <ApplyHead/>
      <Container>
        <BackButton onClick={() => navigate(-1)} style={{ display: 'flex', alignItems: 'center' }}>
          <FontAwesomeIcon icon={faArrowLeft} style={{ marginRight: '5px' }} />
          돌아가기
        </BackButton>

        <Title> 재난 대응 어플리케이션 개발 백엔드 구해요! </Title>
        <LikeButton onClick={handleLike}>
            <FontAwesomeIcon icon={faHeart} style={{ color: liked ? 'red' : 'grey' }} />
            {liked ? ' 좋아요 취소' : ' 좋아요'}
          </LikeButton>

        <Post>
          <PostDetails>
            <Detail>모집구분: 프로젝트</Detail>
            <Detail>모집인원: 2/4</Detail>
            <Detail>마감일: 2024.07.29</Detail>
            <Detail>진행기간: 3개월</Detail>
            <Detail>진행장소: 강남역</Detail>
            <Detail>신청자수: 백엔드(3), 디자이너(1)</Detail>
            <LastDetail>모집 역할: 백엔드(2), 디자이너(1)</LastDetail>
          </PostDetails>

          <TagsSection>
          <TagButton>AWS</TagButton>
          <TagButton>Blockchain</TagButton>
          </TagsSection>


          <ApplyButton>신청하기</ApplyButton>


        </Post>

        <PostDescription>
          블록체인 DApp을 개발할 예정입니다! 아지 어떤 어플리케이션의 아이디어는 미정이며, 프로젝트 팀원이 모이면 같이 브레인스토밍을 통해 결정할 예정입니다.
          블록체인 DApp을 개발할 예정입니다! 아지 어떤 어플리케이션의 아이디어는 미정이며, 프로젝트 팀원이 모이면 같이 브레인스토
          블록체인 DApp을 개발할 예정입니다! 아지 어떤 어플리케이션의 아이디어는 미정이며, 프로젝트 팀원이 모이면 같이 브레인스토
        </PostDescription>


        <AuthorSection>
          <ChatButton>
            <FontAwesomeIcon icon={faComment} />
            채팅
          </ChatButton>

          <AuthorID> 👤 작성자: 사용자123</AuthorID>
          </AuthorSection>


        <CommentsSection>
          <CommentsTitle>댓글 ({comments.length})</CommentsTitle>
          <CommentsList>
            {comments.map((comment, index) => (
              <Comment key={index}>{comment}</Comment>
            ))}
          </CommentsList>
          <CommentInputWrapper>
            <CommentInput
              value={commentInput}
              onChange={(e) => setCommentInput(e.target.value)}
              placeholder="댓글을 입력해 주세요."
            />
            <CommentButton onClick={handleCommentSubmit}>등록</CommentButton>
          </CommentInputWrapper>
        </CommentsSection>
      </Container>
    </>
  );
};

export default ApplyPage;

const Container = styled.div`
  position: relative;
  padding: 20px;
  margin-top: calc(100vh - 35%);
  min-height: calc(100vh - 250px);
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BackButton = styled.button`
  position: absolute;
  left: 10%;
  top: 5%;
  background: transparent;
  border: none;
  color: #62b9ec;
  font-size: 20px;
  font-weight: bold;
  cursor: pointer;

  &:hover {
    opacity: 0.5;
  }
`;

const Title = styled.div`
  position: absolute;
  left: 10%;
  top: 5%;
