import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import ApplyHead from "../../components/ApplyHead";
import { useNavigate , useParams } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faHeart, faComment } from '@fortawesome/free-solid-svg-icons';
import {  faUser as regularUser } from '@fortawesome/free-regular-svg-icons'; 
import items from '../../data.js'; // 임의 데이터 파일 import



const ApplyPage = () => {
  const navigate = useNavigate(); 
  const {projectId } = useParams(); // URL에서 projectId 가져오기
  const [comments, setComments] = useState([]);
  //usestate : 컴포넌트 상태 관리에 씀
  //첫번째 요소: 현재 상태 값, 두번째 요소 : 상태를 없데이트하는 값 
  const [commentInput, setCommentInput] = useState('');
  const [project, setProject] = useState(null);
  const [liked, setLiked] = useState(false);



const fetchProjectDetails = () => {
  // projectId를 'FEED#5678' 형태로 변환
  const formattedProjectId = projectId.slice(0, 4) + '#' + projectId.slice(4);

  console.log("Formatted Project ID:", formattedProjectId); // 변환된 projectId 확인
  const selectedProject = items.find(item => item.pk === formattedProjectId); // pk와 비교

  console.log("Selected Project:", selectedProject); // 찾은 프로젝트
  setProject(selectedProject);
};


  // const fetchProjectDetails = () => {

  //   console.log("Project ID from URL:", projectId); // URL에서 가져온 projectId
  //   const selectedProject = items.find(item => item.pk === projectId);

