      if (project) {
        setProject(prevProject => ({
          ...prevProject,
          likescount: newLikedState ? prevProject.likescount + 1 : prevProject.likescount - 1
        }));
      }

      return newLikedState; // 새로운 좋아요 상태 반환
    });
  };


