import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom'; 
import items from '../data.js'; // 임의 데이터 파일 import
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser as regularUser } from '@fortawesome/free-regular-svg-icons'; 
import { faHeart as regularHeart } from '@fortawesome/free-regular-svg-icons'; 
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

const Section2 = ({ onApplyClick }) => {
  const [allProjects, setAllProjects] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const itemsPerPage = 6; // 페이지당 아이템 수
  const navigate = useNavigate();

  const fetchAllProjects = async () => {
    const response = await axios.get('/data.json');
    setAllProjects(response.data);
  };

  useEffect(() => {
    fetchAllProjects();
  }, []);

  const handleProjectClick = (project) => {
    const modifiedPk = project.pk.replace('#', '');
    navigate(`/ApplyPage/${modifiedPk}`);
  };

  const totalPages = Math.ceil(allProjects.length / itemsPerPage);
  const displayedProjects = allProjects.slice(currentPage * itemsPerPage, (currentPage + 1) * itemsPerPage);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  return (
    <SectionWrapper>
      <SectionTitle>프로젝트 목록</SectionTitle>
      <ProjectList>
        {displayedProjects.map((project, index) => (
          <ProjectCard key={index}>
            <ProjectOwner>
              <FontAwesomeIcon icon={regularUser} style={{ fontSize: '20px', lineHeight: '1.2', marginRight: '6px' }} />
              {project.creatorID}
            </ProjectOwner>
            <ProjectLikes>
              <FontAwesomeIcon icon={regularHeart} style={{ color: 'white', marginRight: '4px' }} />
              {project.likesCount}
            </ProjectLikes>
            <ProjectTitle>{project.title}</ProjectTitle>
            <Content>{project.content}</Content>
            <ProjectInfo>
              <Tags>
                {project.tags.map((tag, tagIndex) => (
                  <Tag key={tagIndex}>{tag}</Tag>
                ))}
              </Tags>
              <Details>
                모집인원 | {project.recruitmentNum}명
              </Details>
              <Details>
                마감일 | {new Date(project.deadline).toLocaleDateString()}
              </Details>
            </ProjectInfo>
            <ApplyButton onClick={() => handleProjectClick(project)}>신청하기</ApplyButton>
          </ProjectCard>
        ))}
      </ProjectList>
      <Pagination>
        {Array.from({ length: totalPages }).map((_, pageIndex) => (
          <PageNumber
            key={pageIndex}
            onClick={() => handlePageChange(pageIndex)}
            active={pageIndex === currentPage}
          >
            {pageIndex + 1}
          </PageNumber>
        ))}
      </Pagination>
    </SectionWrapper>
  );
};

// 스타일 컴포넌트
const SectionWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  //  width: calc(100% / 2 + 100px);
  margin-top: 50px;
  margin-bottom: 40px;

  @media (max-width: 1200px) {
    margin-top: 40px;
    margin-bottom: 30px;
  }

  @media (max-width: 768px) {
    margin-top: 30px;
    margin-bottom: 20px;
  }

  @media (max-width: 480px) {
    margin-top: 20px;
    margin-bottom: 10px;
  }
`;

const SectionTitle = styled.h2`
  font-size: 24px;
  margin-top: 20px;
  margin-bottom: 20px;
`;

const ProjectList = styled.div`
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  justify-content: center;
  // width: 50%;
  width: calc(100% / 2 + 100px);
  max-height: 150vh;
  max-width: 1200px;

  @media (max-width: 1200px) {
    width: 80%;
  }

  @media (max-width: 768px) {
    width: 90%;
  }

  @media (max-width: 480px) {
    width: 100%;
  }
`;

const ProjectCard = styled.div`
  position: relative;
  border: 2px solid #A0DAFB;
  border-radius: 30px 30px 1px 30px;
  padding: 15px;
  margin: 20px 10px;
  margin-right: 30px;
  width: 420px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: white;
  min-height: 200px;
  max-height: 500px;
`;

const ProjectOwner = styled.div`
  font-weight: bold;
  text-align: left;
  color: #858585;
  margin-left: 10px;
`;

const ProjectLikes = styled.div`
  position: absolute;
  border: 1px solid #ddd;
  border-radius: 15px;
  padding: 2px 8px;
  color: white;
  font-weight: bold;
  background-color: #C4C4C4;
  transform: translate(350px,-25px);
`;

const ProjectTitle = styled.h3`
  font-size: 20px;
  margin-top:20px;
  margin-bottom: 30px;
`;

const Content = styled.div`
  font-size: 16px;
  margin-bottom: 10px;
  min-height: 50px;
  max-height: 100px;
  overflow: hidden;
  color: #858585;
`;

const ProjectInfo = styled.div`
  font-size: 14px;
  color: #777;
`;

const Tags = styled.div`
  display: flex;
  padding-top: 10px;
  margin-bottom: 5px;
  align-items: space-between;
`;

const Tag = styled.div`
  margin-bottom: 10px;
  margin-left: 5px;
  margin-right: 5px;
  border: 1px solid #ddd;
  padding: 5px 10px;
  border-radius: 14px 14px 1px 14px; 
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: white;
  border-color: rgba(160, 218, 251);
  color: #0A8ED9;
`;

const Details = styled.div`
  text-align: left;
  margin-bottom: 5px;
`;

const ApplyButton = styled.button`
  margin-top: 5px;
  margin-left: 5px;
  border: 1px solid #ddd;
  border-radius: 15px;
  padding: 8px;
  width: calc(100% / 4);
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: #62B9EC;
  color: white;
  font-weight: bold;
  cursor: pointer;
  transform: translateX(130px);
  border-radius: 14px 14px 1px 14px; 

  &:hover {
    background-color: #A0DAFB;
  }
`;

const Pagination = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 20px;
`;

const PageNumber = styled.button`
  color: ${({ active }) => (active ? '#6AB2E1' : 'black')};
  // margin: 20px 5px;
  margin-top: 40px;
  margin-left: 5px;
  cursor: pointer;
  border: none;
  background: none;
  font-weight: bold;
  font-size: 16px;
`;

export default Section2;
