import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBell as regularBell, faCommentDots as regularComment, faUser as regularUser } from '@fortawesome/free-regular-svg-icons'; // 라인 아이콘 임포트


const Head = () => {


    const [visible, setVisible] = useState(true);

      //로컬 스토리지에서 초기값 가져오기
  const [toggleActive, setToggleActive] = useState(() => {
    // 로컬 스토리지에서 초기값 가져오기
    const storedToggleState = localStorage.getItem('toggleActive');
    return storedToggleState !== null ? JSON.parse(storedToggleState) : false;
  });


      const handleToggleChange = () => {
    setToggleActive((prev) => !prev);
  };   // 이전 상태 기반



  return (
    <HeadWrapper visible={visible}>
     <Logo>
      <img
          alt="Disney Plus Logo"
          src="/images/logo1.png"
        onClick={() => (window.location.href = "/")}/>
      </Logo>


    <ToggleContainer>
      <ToggleLabel active={toggleActive}>STUDY</ToggleLabel>
      <ToggleSwitch type="checkbox" checked={toggleActive} onChange={handleToggleChange} />
      <ToggleLabel active={!toggleActive}>PROJECT</ToggleLabel>
    </ToggleContainer>


    <UserActions>
        <UserIcon  className="bell" > <FontAwesomeIcon icon={regularBell} size="15px" /></UserIcon>
        <UserIcon className="comment">     <FontAwesomeIcon icon={regularComment} size="15px" /></UserIcon>
        <UserIcon  className="user" >  <FontAwesomeIcon icon={regularUser} size="15px" /></UserIcon>
        <LogoutButton>   로그아웃</LogoutButton>
    </UserActions>
  </HeadWrapper>
  )
}

export default Head;


const HeadWrapper = styled.nav`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 20%;
  background-color: #ffffff;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 36px;
  z-index: 3;
  transition: transform 0.5s ease;
  transform: translateY(${props => (props.visible ? '0' : '-100%')});
`;

const Logo = styled.div`
  position: absolute;
  top:7%;

  img {
    width: 90%; 
    max-width: 300px; 
    height: auto; 
    cursor: pointer;
  }
`;
const ToggleContainer = styled.div`
  display: flex;
  align-items: center;
  position: absolute;
  top: 60%;
  cursor: pointer;
  font-size: 24px;
`;

const ToggleLabel = styled.label`
  margin: 0 10px;
  font-size: 16px;
  font-weight: bold;
  color: ${props => (props.active ? '#aaa' : '#0080ff')};
`;

const ToggleSwitch = styled.input`
  appearance: none;
  width: 50px;
  height: 30px;
  background: #ccc; 
  border-radius: 30px; 
  position: relative;
  cursor: pointer;
  outline: none;

  &:checked {
    background: #62B9EC; 
  }

  &:checked::before {
    transform: translateX(24px); 
  }

  &::before {
    content: '';
    position: absolute;
    width: 26px; 
    height: 26px; 
    top: 7%;
    border-radius: 50%; 
    background: white; 
    transition: transform 0.2s; 
  }
`;



const UserActions = styled.div`
  position: absolute;
  top: 15%;
  right: 5%;
  display: flex;
  align-items: center;
  color: #0080ff;
`;



const LogoutButton = styled.div`
  border: 1px none #0080ff;
  background-color: #3563E9;  
  margin: 0 10px;
  padding: 8px 16px;
  border-radius: 5px;
  font-size: 16px;
  color: white;
  cursor: pointer;

  &:hover {
    color: #0080ff;
  }
`;


const UserIcon = styled.div`
  margin: 0 10px;
  font-size: 25px;
  cursor: pointer;
  color: grey;

  &:hover {
    color: #D9D9D9;
  }

`;
