import React, { useState } from 'react';
import styled from 'styled-components';

const Dropdown = ({ options, placeholder }) => {
    const [isOpen, setIsOpen] = useState(false);
    const [selectedOption, setSelectedOption] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const [isFocused, setIsFocused] = useState(false); // 포커스 상태 추가

    const filteredOptions = options.filter(option =>
        option.label.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const handleOptionClick = (option) => {
        setSelectedOption(option.label);
        setIsOpen(false);
        setSearchTerm('');
    };

    const handlePlaceholderClick = () => {
        setIsOpen(true);
        setSearchTerm(''); // 플레이스홀더 클릭 시 검색어 초기화
    };

    const handleInputChange = (e) => {
        setSearchTerm(e.target.value);
    };

    return (
        <DropdownWrapper>
            <DropdownHeader 
            onClick={handlePlaceholderClick} 
            isSelected={!!selectedOption} // 선택된 옵션이 있는지 확인
            isFocused={isFocused} // 포커스 상태 전달
            onFocus={() => setIsFocused(true)} // 포커스 상태 설정
            onBlur={() => setIsFocused(false)} // 포커스 해제 시 상태 초기화

            >


                <SearchInput
                    type="text"
                    placeholder={selectedOption || placeholder}
                    value={searchTerm}
                    onChange={handleInputChange}
                    onFocus={() => setIsOpen(true)} // 검색 입력란 클릭 시 드롭다운 열기
                    isSelected={!!selectedOption} // 선택된 옵션 여부 전달
                />

            </DropdownHeader>
            {isOpen && (
                <DropdownList>

                    {filteredOptions.length > 0 ? (
                        filteredOptions.map(option => (
                            <DropdownItem 
                                key={option.value} 
                                onClick={() => handleOptionClick(option)}
                            >
                                {option.label}
                            </DropdownItem>
                        ))
                    ) : (
                        <NoOptions>옵션이 없습니다.</NoOptions>
                    )}
                </DropdownList>
            )}
        </DropdownWrapper>
    );
};

// 스타일 컴포넌트 정의
const DropdownWrapper = styled.div`
    position: relative;
    width: 300px; /* 드롭다운 너비 설정 */
`;

const DropdownHeader = styled.div`
    border: 2px solid ${({ isFocused }) => (isFocused ? '#007BFF' : '#A0DAFB')}; /* 포커스 상태에 따라 테두리 색상 변경 */
    border-radius: 15px;
    padding:8px;   
    cursor: pointer;
    background-color: #fff;
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-left: 20px;
    font-size: 14px;
    color: grey;
    color: ${({ isSelected }) => (isSelected ? 'black' : 'grey')}; /* 선택 여부에 따라 글씨 색상 변경 */


`;

const DropdownList = styled.div`
    position: absolute;
    border: 2px solid #A0DAFB;
    border-radius: 15px;
    background: white;
    width: 100%;
    max-height: 200px;
    overflow-y: auto;
    z-index: 1000;
`;

const SearchInput = styled.input`
    padding: 4px;
    border: none;
    width: 100%;
    box-sizing: border-box;
    outline: none; /* 포커스 시 외곽선 제거 */
    color: ${({ isSelected }) => (isSelected ? 'black' : 'grey')}; /* 선택된 옵션에 따라 글씨 색상 변경 */
`;

const DropdownItem = styled.div`
    padding: 10px;
    cursor: pointer;

    &:hover {
        background-color: #f0f0f0;
    }
`;

const NoOptions = styled.div`
    padding: 10px;
    color: #aaa;
`;

export default Dropdown;
