import React, { useState } from 'react';
import styled from 'styled-components';

const TagSelector = ({ options, onTagSelect }) => {
    const [selectedOptions, setSelectedOptions] = useState([]);

    const handleTagClick = (option) => {
        if (selectedOptions.includes(option.label)) {
            setSelectedOptions(selectedOptions.filter(selected => selected !== option.label));
        } else {
            if (selectedOptions.length < 8) {
                setSelectedOptions([...selectedOptions, option.label]);
                onTagSelect(option); // 선택된 태그 전달
            }
        }
    };

    return (
        <TagContainer>
            {options.map(option => (
                <Tag
                    key={option.value}
                    onClick={() => handleTagClick(option)}
                    isSelected={selectedOptions.includes(option.label)}
                >
                    <TagIcon>{/* 여기서 아이콘을 추가할 수 있습니다 */}</TagIcon>
                    {option.label}
                </Tag>
            ))}
        </TagContainer>
    );
};

const TagContainer = styled.div`
    display: flex;
    flex-wrap: wrap;
    gap: 10px; /* 태그 간의 간격 */
`;

const Tag = styled.div`
    background-color: ${({ isSelected }) => (isSelected ? '#A0DAFB' : 'white')};
    border: 2px solid #A0DAFB;
    border-radius: 15px;
    padding: 8px 12px;
    cursor: pointer;
    display: flex;
    align-items: center;
    transition: background-color 0.3s;

    &:hover {
        background-color: rgba(160, 218, 251, 0.5);
    }
`;

const TagIcon = styled.span`
    margin-right: 5px; /* 아이콘과 태그 텍스트 간의 간격 */
`;

export default TagSelector;
