// Section2.js
import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import items from '../data.js'; // 임의 데이터 파일 import

const Section2 = ({ onApplyClick }) => {
  const [allProjects, setAllProjects] = useState([]);

  const fetchAllProjects = () => {
    setAllProjects(items);
    console.log("Loaded projects:", items);
  };

  useEffect(() => {
    fetchAllProjects();
  }, []);

  return (
    <SectionWrapper>
      <SectionTitle>프로젝트 목록</SectionTitle>
      <ProjectList>
        {allProjects.slice(3, 7).map((project, index) => (
          <ProjectCard key={index}>
            <ProjectOwner>{project.creatorid}</ProjectOwner>
            <ProjectTitle>{project.title}</ProjectTitle>
            <Content>{project.content}</Content>
            <ProjectInfo>
              <Tags>
                {project.tags.map((tag, tagIndex) => (
                  <Tag key={tagIndex}>{tag}</Tag>
                ))}
              </Tags>
              <Details>
                모집인원: {project.recruitmentnum}명 | 마감일: {new Date(project.deadline).toLocaleDateString()}
              </Details>
            </ProjectInfo>
            <ApplyButton onClick={() => onApplyClick(project)}>신청하기</ApplyButton>
          </ProjectCard>
        ))}
      </ProjectList>
    </SectionWrapper>
  );
};

// 스타일 컴포넌트
const SectionWrapper = styled.div`
  width: calc(100% / 2 + 100px);
  margin-top: 50px;
`;

const SectionTitle = styled.h2`
  font-size: 24px;
  margin-bottom: 20px;
`;

const ProjectList = styled.div`
  display: flex;
  flex-direction: column;
`;

const ProjectCard = styled.div`
  border: 2px solid #A0DAFB;
  border-radius: 30px 30px 1px 30px;
  padding: 15px;
  margin-bottom: 20px;
  margin-top: 20px;
  width: calc(100% / 2 - 100px);
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: white;
`;

const ProjectOwner = styled.div`
  font-weight: bold;
`;

const ProjectTitle = styled.h3`
  font-size: 20px;
`;

const Content = styled.div`
  font-size: 12px;
  margin-bottom: 10px;
`;

const ProjectInfo = styled.div`
  font-size: 14px;
  color: #777;
`;

const Tags = styled.div`
  display: flex;
  margin-bottom: 5px;
  align-items: space-between;
`;

const Tag = styled.div`
  margin-bottom: 5px;
  margin-left: 5px;
  border: 1px solid #ddd;
  border-radius: 15px;
  padding: 5px;
  width: calc(100% / 4);
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: white;
  border-color: rgba(160, 218, 251);
  color: #0A8ED9;
`;

const Details = styled.div``;

const ApplyButton = styled.button`
  margin-top: 5px;
  margin-left: 5px;
  border: 1px solid #ddd;
  border-radius: 15px;
  padding: 5px;
  width: calc(100% / 4);
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: #62B9EC;
  color: white;
  font-weight: bold;
  cursor: pointer;

  &:hover {
    background-color: #A0DAFB;
  }
`;

export default Section2;
