import React, { useState } from 'react';
import styled from 'styled-components';
import Head from "../../components/Head";
import { useNavigate } from 'react-router-dom';

const WritePage = () => {
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [participants, setParticipants] = useState(0);
  const [deadline, setDeadline] = useState('');
  const [progress, setProgress] = useState('');
  const [projectRole, setProjectRole] = useState('');
  const [description, setDescription] = useState('');

  const handleSave = () => {
    // 저장 로직 추가
    console.log({
      title,
      participants,
      deadline,
      progress,
      projectRole,
      description,
    });
    navigate('/'); // 메인 페이지로 이동
  };

  return (
    <>
      <Head />
      <WriteWrapper>
        <BackButton onClick={() => navigate(-1)}>◀ 돌아가기</BackButton>

        <Form>
          {/* <Label>제목을 적어주세요</Label> */}
          <TitleInput>
          <InputField
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="제목을 적어주세요"
          />

          </TitleInput>
          </Form>


        <Container>
        <Form> 

            <InputWrapper>
            <Title>
          <Label>모집구분 | 프로젝트</Label>
          {/* 모집구분 관련 입력 필드 추가 */}
          </Title> 
          </InputWrapper>

        <InputWrapper>
          <Label>모집인원</Label>
          <Input
            type="number"
            value={participants}
            onChange={(e) => setParticipants(e.target.value)}
          />
          </InputWrapper>


          <InputWrapper>

          <Label>마감일</Label>
          <Input
            type="date"
            value={deadline}
            onChange={(e) => setDeadline(e.target.value)}
          />
          </InputWrapper>

          <InputWrapper>

          <Label>진행장소</Label>
          <Input
            value={progress}
            onChange={(e) => setProgress(e.target.value)}
            placeholder="진행장소"
          />
          </InputWrapper>




          <InputWrapper>   
          <Label>모집 역할</Label>
          <Input
            value={projectRole}
            onChange={(e) => setProjectRole(e.target.value)}
            placeholder="모집 역할"
          />
          </InputWrapper>

        </Form>
        </Container>

        <Body>
        <Form>

          <TextArea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="본문을 작성해주세요"
          />

        </Form>
        </Body>
        <SaveButton onClick={handleSave}>저장하기</SaveButton>
      </WriteWrapper>
    </>
  );
};

export default WritePage;

// Styled Components
const WriteWrapper = styled.div`
  padding: 20px;
  margin-top: calc(100vh - 35%);
  min-height: calc(100vh - 250px);
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BackButton = styled.button`
  position: absolute;
  left: 10%;
  margin-bottom: 20px;
`;

const Form = styled.div`
//   display: flex;
//   flex-direction: column;
  gap: 10px;
`;

const Label = styled.label`
  font-weight: bold;
`;

const Title = styled.label`
  font-weight: bold;
  left: 20%;
`;


const TitleInput = styled.div`
//   display: flex;
  position: absolute;
  left:20%;
  top: 30%;
  align-items: center;
  border-bottom:3px solid #ccc;
  padding: 5px 0;
  cursor: text;

  &:focus-within {
    border-bottom: 3px solid #62b9ec; /* 포커스 시 색상 변경 */
  }
`;

const InputField = styled.input`
//   flex: 1;
  border: none;
  outline: none;
  padding: 5px 0;
  font-size:25px;
  &::placeholder {
    color: #aaa;
  }
`;
const Container = styled.div`
//   display: flex;
//   flex-wrap: wrap;
  justify-content: space-between;
  width: calc(100% / 2 + 100px); /* 여백을 고려하여 조정 */
  margin-top:100px;
  padding: 20px;
  border: 1px solid #ddd;
  border-radius: 15px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: rgba(160, 218, 251, 0.5);
// `;


const InputWrapper = styled.div`
//   flex: 1 1 40%; /* 각 입력 필드가 45% 너비를 가지도록 설정 */
  min-width: 1000px; /* 최소 너비 설정 */
  margin-bottom: 20px;
`;


const Input = styled.input`
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 5px;
`;

const Body = styled.div`
//   display: flex;
//   flex-wrap: wrap;
//   justify-content: space-between;
  width: calc(100% / 2 + 100px); /* 여백을 고려하여 조정 */
//  height: calc(100% / 2 + 100px);
//   height: 100%;
//   margin-top: 30px;
//   padding: 20px;
//   border: 1px solid #ddd;
//   border-radius: 15px;
//   box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
//   background-color: rgba(160, 218, 251, 0.5);
`;


const TextArea = styled.textarea`
 border: none;
  // border: 1px solid #ddd;
  border-radius: 15px;
  width: calc(100% / 2 + 100px); /* 여백을 고려하여 조정 */
  outline: none;
  margin-top: 30px;
  font-size: 20px;
//   background: transparent;
  background-color: rgba(160, 218, 251, 0.5);
//   resize: none; /* 크기 조정 비활성화 */
  padding: 20px; /* 패딩 추가 */
  height: 500px; /* 높이 설정 */
  width: 100%; /* 너비 설정 */

  &::placeholder {
    color: #aaa;
    opacity: 1; /* placeholder의 불투명도 설정 */
  }

  &:focus {
    outline: none; /* 포커스 시 외곽선 제거 */
  }
`;




const SaveButton = styled.button`
  margin-top: 20px;
  padding: 10px;
  border: none;
  border-radius: 5px;
  background-color: #62b9ec;
  color: white;
  font-weight: bold;
  cursor: pointer;

  &:hover {
    background-color: #a0dafb;
  }
`;
