import React from 'react';
import styled from 'styled-components';

const ProjectDetail = ({ project }) => {
  // 기본값 설정: applicants가 없을 경우 빈 배열로 설정
  const applicants = project.applicants || []; 

  return (
    <DetailContainer>
      <DetailHeader>
        <h2>{project.title}</h2>
        <Button>모집완료</Button>
      </DetailHeader>
      <DetailInfo>
        <p>게시자: {project.creatorID}</p>
        <p>게시일: {project.createdAt}</p>
        <p>마감일: {project.dueDate}</p>
        <p>진행기간: {project.duration}</p>
        <Tags>
          {project.tags.map((tag, index) => (
            <Tag key={index}>{tag}</Tag>
          ))}
        </Tags>
      </DetailInfo>
      <ApplicationStatus>
        <h3>신청현황</h3>
        {/* 신청자 목록을 표시하는 부분 */}
        {applicants.length > 0 ? ( // applicants가 비어있지 않은 경우
          applicants.map((applicant, index) => (
            <Applicant key={index}>
              <span>{applicant.name}</span>
              <StatusButton>{applicant.status}</StatusButton>
            </Applicant>
          ))
        ) : (
          <p>신청자가 없습니다.</p> // 신청자가 없을 경우 메시지 표시
        )}
      </ApplicationStatus>
    </DetailContainer>
  );
};

// Styled components
const DetailContainer = styled.div`
  border: 1px solid #ccc;
  padding: 20px;
  margin-top: 20px;
`;

const DetailHeader = styled.div`
  display: flex;
  justify-content: space-between;
`;

const DetailInfo = styled.div`
  margin: 10px 0;
`;

const Tags = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const Tag = styled.span`
  background: #e0e0e0;
  margin: 5px;
  padding: 5px 10px;
  border-radius: 5px;
`;

const ApplicationStatus = styled.div`
  margin-top: 20px;
`;

const Applicant = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 5px 0;
`;

const StatusButton = styled.button`
  background: blue;
  color: white;
  border: none;
  padding: 5px;
  border-radius: 5px;
`;

const Button = styled.button`
  background: green;
  color: white;
  border: none;
  padding: 10px;
  border-radius: 5px;
`;

export default ProjectDetail;
